import { createSlice, createAction } from '@reduxjs/toolkit';
import { ContextOptions } from '../../providers/context';
import { IMessage } from './types/message';
import { NotificationsMetadata } from './types/theme';

export interface InterfaceState {
  context: ContextOptions | null;
  messages: {
    data: Array<IMessage>;
    current: number;
    total: number | null;
    unreadCount: number;
  };
  messagesToMarkAsRead: Array<IMessage>;
  switchValue: boolean;
  windowValue: boolean;
  error?: {};
  loading: boolean;
  viewAllValue: boolean;
  metadata?: NotificationsMetadata;
  pollingFrequency: number;
}

export const idToArray = (id: string | Array<string>) => (typeof id === 'string' ? [id] : id);

const changeStatus = (
  messages: InterfaceState['messages'],
  id: string | Array<string>,
  from: string,
  to: string,
  updateCount: boolean = true,
) => {
  const ids = idToArray(id);
  return messages.data.map((message) => {
    if (ids.includes(message.id)) {
      if (updateCount) {
        if (message.status === 'Unread') {
          messages.unreadCount = messages.unreadCount - 1;
        } else {
          messages.unreadCount = messages.unreadCount + 1;
        }
      }

      return { ...message, status: message.status === from ? to : from };
    }
    return message;
  });
};

const changeIsPinned = (messages: Array<IMessage>, id: string | Array<string>) => {
  const ids = idToArray(id);
  return messages.map((message) => (ids.includes(message.id) ? { ...message, isPinned: !message.isPinned } : message));
};

export const initialState: InterfaceState = {
  context: null,
  messages: { data: [], current: 0, total: null, unreadCount: 0 },
  messagesToMarkAsRead: [],
  switchValue: false,
  windowValue: false,
  loading: false,
  error: {},
  viewAllValue: false,
  pollingFrequency: 5000,
};

const { name, actions, reducer } = createSlice({
  name: 'notification',
  initialState,
  reducers: {
    setContext: (state, { payload }) => {
      state.context = payload;
    },
    startLoading: (state) => {
      state.loading = true;
    },
    finishLoading: (state) => {
      state.loading = false;
    },
    setPollingFrequency: (state, { payload }) => {
      state.pollingFrequency = payload;
    },
    handlePolledNotifications: (state, { payload }) => {
      const pulledNotifications: IMessage[] = payload.notifications;
      const newNotifications = pulledNotifications.filter((n) => {
        const foundNotification = state.messages.data.find((e) => e.id === n.id);
        return !foundNotification;
      });

      const newData = [...newNotifications, ...state.messages.data];
      state.messages = {
        data: newData,
        current: newData.length,
        total: payload.total,
        unreadCount: payload.unreadCount,
      };
    },
    loadMoreDataSuccess: (state, { payload }) => {
      state.messages = { ...payload, data: state.messages.data.concat(payload.data) };
    },
    loadItemFailedAction: (state, { payload }) => {
      state.error = { ...state.error, [payload.name]: payload.error };
    },
    loadMetadataSuccess: (state, { payload }) => {
      state.error = {};
      state.metadata = payload;
    },
    markAsUnread: {
      prepare: (props: string) => ({ payload: props }),
      reducer: (state, { payload }) => {
        state.messages.data = changeStatus(state.messages, payload, 'Read', 'Unread');
      },
    },
    markAllAsRead: (state) => {
      const unreadMessages = state.messages.data.filter((m) => m.status === 'Unread');
      state.messages.unreadCount = 0;
      state.messagesToMarkAsRead = unreadMessages;
    },
    markAllAsReadButton: (state) => {
      state.messages.data = state.messages.data.map((m) => ({ ...m, status: 'Read' }));
      state.messages.unreadCount = 0;
      state.messagesToMarkAsRead = [];
    },
    pinMessage: (state, { payload }) => {
      state.messages.data = changeIsPinned(state.messages.data, payload);
    },
    clearMessagesToMarkAsRead: (state) => {
      const idsToMarkAsRead = state.messagesToMarkAsRead.map((m) => m.id);
      state.messages.data = changeStatus(state.messages, idsToMarkAsRead, 'Unread', 'Read', false);
      state.messagesToMarkAsRead = [];
    },
    deleteMessage: {
      prepare: (props: string) => ({ payload: props }),
      reducer: (state, { payload }) => {
        const ids = idToArray(payload);
        state.messages.data = state.messages.data.filter(({ id, status }) => {
          if (ids.includes(id)) {
            if (status === 'Unread') {
              state.messages.unreadCount = state.messages.unreadCount - 1;
            }
            return false;
          }
          return true;
        });
      },
    },
    switchToggle: (state) => {
      state.switchValue = !state.switchValue;
    },
  },
});
export const loadData = createAction(`${name}/loadData`);

export const loadMoreData = createAction(`${name}/loadMoreData`, (offset?: number) => ({ payload: offset }));

export type LoadMoreDataProps = ReturnType<typeof loadMoreData>;

export const loadMetadata = createAction(`${name}/loadMetaData`);

interface UpdateStatus {
  message: IMessage;
  status: string;
}

export const updateMessageStatus = createAction<UpdateStatus>(`${name}/updateMessageStatus`);

export { reducer };

export const {
  setContext,
  setPollingFrequency,
  pinMessage,
  switchToggle,
  markAsUnread,
  markAllAsRead,
  markAllAsReadButton,
  startLoading,
  finishLoading,
  clearMessagesToMarkAsRead,
  deleteMessage,
  handlePolledNotifications,
  loadMoreDataSuccess,
  loadItemFailedAction,
  loadMetadataSuccess,
} = actions;
import React from 'react';
import { connect } from 'react-redux';
import classnames from 'classnames';
