const HARDCODED_BOX_HEIGHT = 405;

interface IProps {
  context: ContextOptions;
  isDemonstration?: boolean;
  pollingFrequency: number;
}

class NotificationsContainer extends React.Component<IProps & DispatchProps & StateToProps> {
  private node: React.LegacyRef<HTMLDivElement> | any;

  state = {
    showMoreButton: false,
    isNotificationsWindowOpened: false,
  };

  async componentDidMount() {
    const { context, setContext, loadData, loadMetadata, setPollingFrequency, pollingFrequency } = this.props;
    await setContext(context);
    setPollingFrequency(pollingFrequency);
    loadData();
    loadMetadata();
    document.addEventListener('mousedown', this.onClickOut);
  }

  componentDidUpdate({ messages: prevMessages }: IProps & DispatchProps & StateToProps) {
    const { messages } = this.props;
    if (prevMessages.current !== messages.current) {
      this.setState({ showMoreButton: false, loading: false });
    }
  }

  componentWillUnmount() {
    document.removeEventListener('mousedown', this.onClickOut);
  }

  handleDownloadData = async () => {
    const { loading } = this.props;
    const { loadMoreData, messages } = this.props;
    if (!loading) {
      loadMoreData(messages.current);
    }
  };

  onClickOut = (event: MouseEvent) => {
    const target = event.target as HTMLDivElement;

    this.node && !this.node.contains(target) && target.id !== 'windowItem' && this.closeNotificationsWindow();
  };

  async closeNotificationsWindow() {
    const { clearMessagesToMarkAsRead } = this.props;

    clearMessagesToMarkAsRead();
    this.setState({ isNotificationsWindowOpened: false });
  }

  shouldMarkAsReadOnOpen() {
    const { metadata } = this.props;
    return (
      metadata &&
      metadata.channelsConfig &&
      metadata.channelsConfig.web &&
      metadata.channelsConfig.web.markAsReadOnOpen === 'True'
    );
  }

  async openNotificationsWindow() {
    const { markAllAsRead } = this.props;

    this.shouldMarkAsReadOnOpen() && markAllAsRead();
    this.setState({ isNotificationsWindowOpened: true });
  }

  async handleNotificationsWindowToggle() {
    // If we are opening
    if (!this.state.isNotificationsWindowOpened) {
      this.openNotificationsWindow();
    } else {
      // We are closing
      this.closeNotificationsWindow();
    }
  }

  render() {
    const {
      metadata,
      newMessageCount,
      severities,
      pinMessage,
      markAsUnread,
      // markAllAsRead,
      markAllAsReadButton,
      deleteMessage,
      preparedMessages,
      messagesToMarkAsRead,
      context,
    } = this.props;

    const { isNotificationsWindowOpened } = this.state;

    const contextTheme = context && context.theme;
    const metadataTheme = metadata && metadata.theme;
    const isInfinite =
      contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsPaginationType') === 'infinite';
    const showTitle = contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsBoxShowTitle');
    const boxShadow =
      contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsBoxShadow') === true
        ? '0 10px 30px 0 rgba(220, 221, 225, 0.67)'
        : 'none';
    const bellIconType = contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsBellIcon');
    const bellIconSize =
      parseInt(contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsBellIconSizeHeight')) || 35;
    const alertLocation = contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsAlertLocation') || 'top';
    const countShowOrHide =
      contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsAlertBadgeCount') || 'show';
    // const messageHeight = parseInt(contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsRowHeight')) || 96;
    const showBadgeCount =
      contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsAlertBadgeCount') !== 'undefined'
        ? contextOrMetadataTheme(contextTheme, metadataTheme, 'notificationsAlertBadgeCount')
        : false;

    const minBoxHeight = HARDCODED_BOX_HEIGHT;
    const msgCounter =
      messagesToMarkAsRead.length + newMessageCount < 0 ? 0 : messagesToMarkAsRead.length + newMessageCount;
    const badgeMsgCounter = msgCounter > 9 ? '9+' : msgCounter < 0 ? 0 : msgCounter;
    const bigCount = typeof badgeMsgCounter === 'string' && 'big-count';

    if (!metadata && !this.props.isDemonstration) {
      return <React.Fragment />;
    }

    return (
      <ThemeProvider context={this.props.context} themeMetadata={metadataTheme && metadataTheme.styles}>
        <div className='frontegg'>
          <div ref={(node) => (this.node = node)} className='wrap_notifications'>
            <div className='notifications'>
              <span onClick={() => this.handleNotificationsWindowToggle()}>
                <a className='notifications__icon'>
                  <BellIcon icon={bellIconType} width={bellIconSize} height={bellIconSize} />
                </a>
                {msgCounter !== 0 && (
                  <span className={`notifications__count ${alertLocation} `}>
                    {true && (
                      <span className={classnames('notifications__count-number', countShowOrHide, bigCount)}>{badgeMsgCounter}</span>
                    )}
                  </span>
                )}
              </span>
              {isNotificationsWindowOpened ? (
                <div className='notifications__window window' style={{ boxShadow: `${boxShadow}` }}>
                  {showTitle && (
                    <div className='window__header'>
                      <span className='window__title'>NOTIFICATIONS</span>
                    </div>
                  )}
                  <div className='window__header'>
                    <div className='window__msg-counter'>{msgCounter} unread</div>
                    {!this.shouldMarkAsReadOnOpen() && (
                      <div className='window__readAll-button' onClick={() => markAllAsReadButton()}>
                        Mark all as Read
                      </div>
                    )}
                  </div>

                  <div className='window__main' id='windowMain' style={{ height: minBoxHeight }}>
                    <Visibility
                      continuous={false}
                      once={false}
                      onBottomVisible={() =>
                        isInfinite
                          ? !this.props.loading && this.handleDownloadData()
                          : !this.props.loading && this.setState({ showMoreButton: true })
                      }
                      onBottomVisibleReverse={() => !isInfinite && this.setState({ showMoreButton: false })}>
                      {preparedMessages.map((message: any, idx: number) => (
                        <Message
                          key={idx}
                          isMessageNew={message.status === 'Unread'}
                          deleteMessage={deleteMessage}
                          isDemonstration={idx === 2 && this.props.isDemonstration}
                          message={message}
                          markAsUnread={markAsUnread}
                          pinMessage={pinMessage}
                          severities={severities}
                          metadataTheme={metadataTheme && metadataTheme}
                          contextTheme={contextTheme && contextTheme}
                        />
                      ))}
                    </Visibility>
                  </div>
                  {isInfinite
                    ? (this.props.loading === true || this.props.isDemonstration === true) && (
                      <div className='window__footer'>
                        <span className='window__footer-description' data-control='viewAllValue'>
                          <Loader size='mini' active inline='centered' />
                        </span>
                      </div>
                    )
                    : (this.state.showMoreButton === true || this.props.isDemonstration === true) && (
                      <div className='window__footer'>
                        <span
                          className='window__footer-description'
                          data-control='viewAllValue'
                          onClick={this.handleDownloadData}>
                          VIEW MORE
                          </span>
                        </div>
                      )}
                </div>
              ) : null}
            </div>
          </div>
        </div>
      </ThemeProvider>
    );
  }
}

const preparedMessages = createSelector(
  ({ messages }) => ({ messages }),
  ({ messages }) => {
    const pinnedMsgs = messages.data.filter((message: IMessage) => message.isPinned === true);
    const unPinnedMsgs = messages.data.filter((message: IMessage) => message.isPinned !== true);
    return pinnedMsgs.length > 0 ? [...pinnedMsgs, ...unPinnedMsgs] : messages.data;
  },
);

const mapSeveritiesByName = createSelector(
  ({ metadata }) => (metadata ? metadata.severities : []),
  // @ts-ignore
  (severities) => severities.reduce((acc, { name, ...data }) => ({ ...acc, [name]: data }), {}),
);
const mapDispatchProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      loadData,
      loadMoreData,
      setContext,
      setPollingFrequency,
      pinMessage,
      markAsUnread,
      deleteMessage,
      markAllAsRead,
      markAllAsReadButton,
      clearMessagesToMarkAsRead,
      loadMetadata,
    },
    dispatch,
  );

const mapStateToProps = (state: InterfaceState) => {
  const { messages, switchValue, windowValue, metadata, loading, messagesToMarkAsRead } = state;
  return {
    messages,
    metadata,
    loading,
    switchValue,
    windowValue,
    messagesToMarkAsRead,
    newMessageCount: state.messages.unreadCount,
    // @ts-ignore
    preparedMessages: preparedMessages(state),
    // @ts-ignore
    severities: mapSeveritiesByName(state),
  };
};
type DispatchProps = ReturnType<typeof mapDispatchProps>;

type StateToProps = ReturnType<typeof mapStateToProps>;

// @ts-ignore
export default connect<StateToProps, DispatchProps, IProps>(mapStateToProps, mapDispatchProps)(NotificationsContainer);
import { ContextOptions, KeyValuePair } from '../providers/context';

async function prepareUrl(context: ContextOptions, url: string, params?: any): Promise<string> {
  const baseUrl = await getBaseUrl(context);
  const paramsToSend = await buildQueryParams(context, params);

  let finalUrl = `${baseUrl}${url}`;
  const hasKeys = Object.keys(paramsToSend).length > 0;
  if (paramsToSend && hasKeys) {
    const urlParams = new URLSearchParams(paramsToSend);
    finalUrl += `?${urlParams}`;
  }

  return finalUrl;
}

export async function Get(context: ContextOptions, url: string, params?: any, resType: 'text' | 'blob' | 'json' = 'json') {
  const headers = await getRequestHeaders(context);
  const finalUrl = await prepareUrl(context, url, params);

  try {
    const response = await fetch(finalUrl, {
      // @ts-ignore
      headers,
      credentials: context.requestCredentials || 'same-origin',
    });

    if (!response.ok) {
      throw new Error(`Error ${response.status} - ${response.statusText}`);
    }
    if (resType === 'json') {
      return response.json();
    }else if (resType === 'blob') {
      return response.blob();
    } else { // (resType === 'text')
      return response.text();
    }
  } catch (e) {
    console.error(`Failed to GET url - ${url} - `, e.message);
    throw e;
  }
}

export async function Post(context: ContextOptions, url: string, body?: any, params?: any) {
  const headersToSend = await getRequestHeaders(context);
