  try {
    const response = await fetch(finalUrl, {
      body: body ? JSON.stringify(body) : null,
      method: 'POST', // *GET, POST, PUT, DELETE, etc.
      // @ts-ignore
      headers: {
        ...headersToSend,
        'Content-Type': 'application/json',
      },
      credentials: context.requestCredentials || 'same-origin',
    });
    if (!response.ok) {
