async function buildQueryParams(context: ContextOptions, params?: any) {
  if (!params) {
    params = {};
  }

  const additionalQueryParams = await getAdditionalQueryParams(context);
  for (const queryParam of additionalQueryParams) {
    params[queryParam.key] = queryParam.value;
  }

  const keys = Object.keys(params);
  for (const key of keys) {
    const value = params[key];
    params[key] = typeof value === 'object' ? JSON.stringify(value) : value;
  }

  return params;
}

async function getAdditionalQueryParams(context: ContextOptions): Promise<KeyValuePair[]> {
  // @ts-ignore
  let output = [];
  if (context.additionalQueryParamsResolver) {
    output = await context.additionalQueryParamsResolver();
  }

  // @ts-ignore
  return output;
}

async function getAdditionalHeaders(context: ContextOptions): Promise<KeyValuePair[]> {
  // @ts-ignore
  let output = [];
  if (context.additionalHeadersResolver) {
    output = await context.additionalHeadersResolver();
  }

  // @ts-ignore
  return output;
}

function getBaseUrl(context: ContextOptions): string {
  let baseUrl = context.baseUrl;
  const prefix = context.urlPrefix || 'frontegg';
  // Append everything we need to the base url
  if (!baseUrl.endsWith('/')) {
    baseUrl += '/';
  }
  if (!baseUrl.endsWith(prefix)) {
    baseUrl += prefix;
  }
  return baseUrl;
}

async function getRequestHeaders(context: ContextOptions) {
  const authToken = await context.tokenResolver();
  const headers = authToken ? { Authorization: `Bearer ${authToken}` } : {};

  const additionalHeaders = await getAdditionalHeaders(context);
  for (const additionalHeader of additionalHeaders) {
    // @ts-ignore
    headers[additionalHeader.key] = additionalHeader.value;
  }

  // @ts-ignore
  headers['x-frontegg-source'] = 'frontegg-react';

  return headers;
}
import { takeEvery, select, put, delay, all } from 'redux-saga/effects';
import Logger from '../../helpers/logger';
import { Get, Post, Put } from '../../helpers/fetch';
import {
  loadData,
  loadMoreData,
  idToArray,
  pinMessage,
  markAsUnread,
  startLoading,
  finishLoading,
  deleteMessage,
  loadItemFailedAction,
  loadMoreDataSuccess,
  handlePolledNotifications,
  markAllAsRead,
  loadMetadata,
  loadMetadataSuccess,
  LoadMoreDataProps,
} from './reducer';
import { IMessage } from './types/message';

function* pollNewNotifications() {
  while (true) {
    const { context, metadata, pollingFrequency } = yield select();

    try {
      const count = (metadata && metadata.channelsConfig.web.pageSize) || 6;

      const response: {
        data: any[];
        total: number;
        unreadCount: number;
      } = yield Get(context, `/notification`, {
        count,
        offset: 0
      });

      yield put(
        handlePolledNotifications({
          notifications: response.data,
          unreadCount: response.unreadCount,
          total: response.total,
        }),
      );
      yield delay(pollingFrequency);
    } catch (err) {
      yield delay(pollingFrequency);
    }
  }
}

function* loadMoreDataFunction({ payload }: LoadMoreDataProps) {
  const { context, messages, metadata } = yield select();
  const count = (metadata && metadata.channelsConfig.web.pageSize) || 6;

  yield put(startLoading());

  const response: {
    data: any[];
    total: number;
    unreadCount: number;
  } = yield Get(context, `/notification`, {
    count,
    offset: messages.data.length
  });

  const newMessages = { data: response.data, current: messages.data.length + response.data.length, total: response.total };

  JSON.stringify(messages) !== JSON.stringify(newMessages) &&
    (yield put(loadMoreDataSuccess({ ...newMessages, unreadCount: response.unreadCount })));

  yield put(finishLoading());
}

function* updateMessageStatusFunction({ payload }: ReturnType<typeof deleteMessage>) {
  const { context, messages } = yield select();
  // @ts-ignore
  const ids = idToArray(payload);
  let sendMessages = messages.data
    .filter(({ id }: IMessage) => ids.includes(id))
    .map(({ id, status }: IMessage) => ({ notificationId: id, status }));
  if (!sendMessages.length) {
    // if can't find elements in the state then we removed it
    sendMessages = ids.map((notificationId) => ({ notificationId, status: 'Archived' }));
  }
  try {
    // @ts-ignore
    yield all[sendMessages.map((props) => Put(context, '/notification/status', props))];
  } catch (e) {
    Logger.error('failed to update notification status - ', e);
  }
}

function* updateMessageIsPinnedFunction({ payload }: ReturnType<typeof deleteMessage>) {
  const { context, messages } = yield select();
  // @ts-ignore
  const ids = idToArray(payload);
  let sendMessages = messages.data
    .filter(({ id }: IMessage) => ids.includes(id))
    .map(({ id, isPinned }: IMessage) => ({
      endpoint: isPinned ? 'pin' : 'unpin',
      payload: { notificationId: id },
    }));

  try {
    // @ts-ignore
    yield all[sendMessages.map((props) => Put(context, `/notification/${props.endpoint}`, props.payload))];
  } catch (e) {
    Logger.error('failed to pin notification - ', e);
  }
}

function* markAllAsReadFunction() {
  const { context } = yield select();

  try {
    yield Post(context, '/notification/status/mark-all-read');
  } catch (err) {
    Logger.error('mark-all-read was failed');
  }
}

function* loadMetadataFunction() {
  const { context } = yield select();
  try {
    const result = yield Get(context, '/metadata', { entityName: 'notifications' });
    if (result.rows.length === 0) {
      return;
    }
    yield put(loadMetadataSuccess(result.rows[0]));
    Logger.debug(result);
  } catch (e) {
    const errorMessage = {
      name: 'metadata',
      error: e,
    };
    Logger.error('failed to load metadata - ', e);
    yield put(loadItemFailedAction(errorMessage));
  }
}

export function* rootSaga() {
  yield takeEvery(loadData, pollNewNotifications);
  yield takeEvery(loadMoreData, loadMoreDataFunction);
  // @ts-ignore
  yield takeEvery([markAsUnread, , deleteMessage], updateMessageStatusFunction);
  yield takeEvery([pinMessage], updateMessageIsPinnedFunction);
  yield takeEvery(markAllAsRead, markAllAsReadFunction);
  yield takeEvery(loadMetadata, loadMetadataFunction);
}
import Logger from './logger';

function getAllCss() {
  var css = "";
  var styleTags = document.getElementsByTagName("style");

  //loop over all the style tags
  for (var i = 0; i < styleTags.length; i++) {
    css += styleTags[i].innerHTML;
  }

  //loop over all the external stylesheets
  for (var i = 0; i < document.styleSheets.length; i++) {
    var currentSheet: any = document.styleSheets[i];
    try {
      //loop over all the styling rules in this external stylesheet      
      for (var e = 0; e < currentSheet.cssRules.length; e++) {
        css += currentSheet.cssRules[e].cssText; //extract all the styling rules
      }
    } catch (e) { /* Continue here. Nothing much we can do */ }
  }

  return css;
}

function getAllLinks() {
  var links = "";
  var linkTags = document.getElementsByTagName("link");

  for (var i = 0; i < linkTags.length; i++) {
    links += linkTags[i].href + ",";
  }

  return links;
}

export function isSemanticUiLoaded(): boolean {
  Logger.log('checking if semantic ui is loaded via css');
  const css = getAllCss();
  const links = getAllLinks();

  const isSemanticUiLoaded =
    (css.indexOf('https://github.com/Semantic-Org/Semantic-UI') > -1) ||
    (css.indexOf('http://www.semantic-ui.com/') > -1) ||
    (links.indexOf('//cdn.jsdelivr.net/npm/semantic-ui') > -1 && links.indexOf('semantic-ui') > -1);

  return isSemanticUiLoaded;
}

