const sagaMiddleware = createSagaMiddleware();

interface NotificationsProps {
  isDemonstration?: boolean;
  pollingFrequency?: number;
}

export default class Notifications extends React.Component<NotificationsProps, {}> {
  store: any;
  task: Task;
  constructor(props: any) {
    super(props);
    this.store = createStore(reducer, initialState, composeWithDevTools(applyMiddleware(sagaMiddleware)));
    this.task = sagaMiddleware.run(rootSaga);
  }

  componentWillUnmount() {
    this.task.cancel();
  }

  render() {
    return (
      <Provider store={this.store}>
        <ContextConsumer>
          {
            (context: ContextOptions) => {
              return <NotificationsContainer
                context={context}
                pollingFrequency={this.props.pollingFrequency || 5000}
                isDemonstration={this.props.isDemonstration} />
            }
          }
        </ContextConsumer>
      </Provider>
    );
  }
}
