const { name, actions, reducer } = createSlice({
	name: 'users',
	initialState,
	reducers: {
		loadItemsAction: state => {
			state.loading = true;
		},
		setContextAction: (state, { payload }) => {
			state.context = payload;
		},
		setIsBusyAction: (state, { payload }) => {
			state.isBusy = payload;
		},
		loadItemFiledAction: (state, { payload }) => {
			state.loading = false;
			state.error = { ...state.error, [payload.name]: payload.error };
		},
		loadItemsSuccessAction: (state, { payload }) => {
			state.loading = false;
			state.totalPages = payload.totalPages;
			state.items = payload.items;
		},
		getAvailableRolesAction: state => {
			state.loadingRoles = true
		},
		availableRolesSuccessAction: (state, { payload }) => {
			state.loadingRoles = false;
			state.roles = payload.roles;
			state.currentUserRoleIds = payload.currentUserRoleIds;
		},
		loadStatsSuccessAction: (state, { payload }) => {
			state.totalItems = payload.totalItems;
			state.addedThisWeek = payload.addedThisWeek;
		},
		inviteAdminSuccessAction: (state, { payload }) => {
			state.items = [payload, ...state.items]
		},
		searchUserAction: (state, { payload }) => {
			state.items = []
			state.pageOffset = 0;
			state.loading = true;
			state.searchFilter = payload;
		},
		fetchMoreItemsActions: (state) => {
			state.pageOffset = state.pageOffset === state.totalPages ? state.totalPages : state.pageOffset + 1;
			state.loading = true;
		},
		fetchNextPageAction: (state, { payload }) => {
			state.pageOffset = payload - 1;
			state.loading = true;
		},
		toggleResetPasswordAction: (state, { payload }) => {
			state.isResetPasswordDialogOpen = payload.isResetPasswordDialogOpen;
			state.isResettingPassword = payload.isResettingPassword;
			state.resetPasswordError = payload.resetPasswordError;
		},
	}
})

