class UserTableRow extends React.Component<UserTableRowProps & DispatchPropsType> {
  private nameInputContainer?: HTMLDivElement;
  private nameInput?: Input;

  state = {
    editedRoleIds: null,
    editedName: this.props.name,
    isEditing: false,
    isDeleting: false,
    newPassword: '',
    confirmNewPassword: '',
    isBusy: false,
    readOnly: false,
    rolesWithAccess: [],
  };

  componentDidMount() {
    document.addEventListener('mousedown', this.handleMouseDown, false);
    this.checkRoleAccess();
  }

  componentWillUnmount() {
    document.removeEventListener('mousedown', this.handleMouseDown, false);
  }

  // @ts-ignore
  private handleMouseDown = (e) => {
    if (this.nameInputContainer && this.nameInputContainer.contains(e.target)) {
      return;
    }

    if (this.nameInput) {
      this.toggleEditName(false);
    }
  };

  private prepareItem() {
    return {
      id: this.props.id,
      email: this.props.email,
      name: this.state.editedName,
      createdAt: this.props.createdAt,
      lastLogin: this.props.lastLogin,
      profileImageUrl: this.props.profileImageUrl,
      roleIds: this.state.editedRoleIds || this.props.roleIds,
    };
  }

  private onCancelDelete = () => {
    this.setState({ isDeleting: false });
  };

  private onConfirmDelete = async () => {
    this.setState({ isDeleting: null, isBusy: true });

    const itemToDelete = this.prepareItem();
    try {
      this.props.onDeleteUser && (await this.props.onDeleteUser(itemToDelete));
    } catch (e) { }

    this.setState({ isBusy: false });
  };

  // @ts-ignore
  onNameChanged = (e) => {
    const name = e.target.value;
    this.setState({ editedName: name });
  };

  editItemName = async () => {
    this.setState({ isBusy: true });
    const itemToEdit = this.prepareItem();

    this.toggleEditName(false);

    try {
      this.props.onEditUser && (await this.props.onEditUser(itemToEdit));
    } catch (e) { }

    this.setState({ isBusy: false });
  };

  toggleEditName = (editMode: boolean) => {
    this.setState({ isEditing: editMode });
    if (editMode === true) {
      this.setState({ editedName: this.props.name });
      setTimeout(() => {
        if (this.nameInput) {
          this.nameInput.select();
        }
      }, 300);
    } else {
      this.nameInput = undefined;
    }
  };

  // @ts-ignore
  onEditNameKeyPress = (e) => {
    if (e.charCode === 13 || e.keyCode === 13) {
      this.editItemName();
    }
  };

