    this.setState({ isDeleting: true });
  };

  onResetPassword = async () => {
    this.setState({ newPassword: '', confirmNewPassword: '' });
    this.props.onToggleResetPassword &&
      this.props.onToggleResetPassword({
        isResetPasswordDialogOpen: true,
        isResettingPassword: false,
        resetPasswordError: '',
      });
  };

  // @ts-ignore
  onConfirmResetPassword = async (e) => {
    const itemToReset = { id: this.props.id, newPassword: this.state.newPassword };
    this.props.onResetPassword && (await this.props.onResetPassword(itemToReset));
