  onCloseResetPassword = async () => {
    this.props.onToggleResetPassword &&
      this.props.onToggleResetPassword({
        isResetPasswordDialogOpen: false,
        isResettingPassword: false,
        resetPasswordError: '',
      });
  };

  onSendActivationEmail = async () => {
    this.setState({ isBusy: true });

    const itemToReset = { id: this.props.id };
    try {
      this.props.onResendActivationEmailAction && (await this.props.onResendActivationEmailAction(itemToReset));
    } catch (e) { }

    this.setState({ isBusy: false });
