    return (
      <Dropdown
        // button
        // className='icon'
        className={'roles ' + (allRolesSelected === true ? 'full' : '')}
        floating
        multiple
        labeled
        disabled={this.state.isBusy || this.state.readOnly}
        closeOnChange
        onChange={(_, data) => this.onRolesChanged(data.value)}
        // onClose={(_, data) => this.onRolesClosed(data.value)}
        value={this.props.roleIds}
        options={roleOptions}
        text='Select role'
      />
    );
  };

  // @ts-ignore
  onRolesChanged = (roles) => {
    this.setState({ editedRoleIds: roles }, async () => {
      const itemToEdit = this.prepareItem();
      this.setState({ isBusy: true });
      this.props.onEditUser && (await this.props.onEditUser(itemToEdit));
      this.setState({ isBusy: false });
    });
  };

  private getRoleLevel(roleId: string) {
    if (!this.props.roles) return 0;
    const roleSettings = this.props.roles.find((role: any) => role.id === roleId) || {};
    return roleSettings.permissionLevel || 0;
  };

  private getMaxRoleLevel(roleIds: string[]) {
    if (!roleIds) return 0;
    // map roleIds array to numeric levels array, using provider roles settings
    const levelsArr: number[] = roleIds.map(roleId => this.getRoleLevel(roleId));
    const maxRoleLevel = levelsArr.length ? Math.max(...levelsArr) : 0;
    return maxRoleLevel;
  }

  private checkRoleAccess() {
    let currentUserRolesIds: string[] = [];

    if (this.props.context.currentUserRoles && this.props.context.currentUserRoles.length > 0) {
      currentUserRolesIds = this.props.context.currentUserRoles;
    } else if (this.props.currentUserRoleIds && this.props.currentUserRoleIds.length > 0) {
      currentUserRolesIds = this.props.currentUserRoleIds;
    }

    const currnetUserRoleLevel = this.getMaxRoleLevel(currentUserRolesIds);
    const tableUserRoleLevel = this.getMaxRoleLevel(this.props.roleIds)
    if (currnetUserRoleLevel < tableUserRoleLevel) {
      this.setState({ readOnly: true });
    }
    if (this.props.roles) {
      const rolesWithAccess = this.props.roles.filter(role => role.permissionLevel <= currnetUserRoleLevel);
      this.setState({ rolesWithAccess });
    }
  }

  renderResetPasswordMenuItem = () => {
    const { id, lastLogin, allowChangingPasswords, context } = this.props;

    if (!lastLogin) {
      return <Dropdown.Item icon='mail' text='Resend activation email' onClick={() => this.onSendActivationEmail()} />;
    }

    if (allowChangingPasswords !== false) {
      return <Dropdown.Item icon='pencil' text='Change password' onClick={() => this.onResetPassword()} />;
    }

    // Allow changing password only it the logged in user is the user that we are actually working on
    if (context.currentUserId && context.currentUserId === id) {
      return <Dropdown.Item icon='pencil' text='Change password' onClick={() => this.onResetPassword()} />;
    }
  };

  render() {
    const { isEditing, isDeleting, editedName } = this.state;
    const {
      email,
      createdAt,
      profileImageUrl,
      lastLogin,
      name,
      error,
      isResetPasswordDialogOpen,
      isResettingPassword,
      resetPasswordError,
    } = this.props;
    const deleteContent = isDeleting && `You are about to remove ${name} from this account. Are you sure?`;

    const { newPassword, confirmNewPassword } = this.state;
    const canResetPassword = newPassword.length > 0 && newPassword === confirmNewPassword;
    const mountNode = document.querySelector('.frontegg .users');
    return (
      <Table.Row className={classnames('user-row', this.state.readOnly ? 'read-only' : '')} disabled={this.state.isBusy || this.state.readOnly}>
        <Table.Cell width='1'>
          <img className='avatar' alt='profile' src={profileImageUrl} />
        </Table.Cell>
        <Table.Cell>
          <div>
            {isEditing ? (
              <div
                ref={(c) => {
                  c && (this.nameInputContainer = c);
                }}>
                <Input
                  value={editedName}
                  className='name-input'
                  ref={(input) => {
                    input && (this.nameInput = input);
                  }}
                  onChange={this.onNameChanged}
                  onKeyPress={this.onEditNameKeyPress}
                />
              </div>
            ) : (
                <p onMouseUp={() => this.toggleEditName(true)} className='name'>
                  {name}
                </p>
              )}
            <p className='email'>{email}</p>
          </div>
        </Table.Cell>

        {error ? (
          <Table.Cell>
            <p>No data available</p>
          </Table.Cell>
        ) : (
            this.props.roles && this.props.roles.length > 0 && <Table.Cell>{this.renderItemRole()}</Table.Cell>
          )}

        <Table.Cell>
          <div>
            <p className='joined-date'>{createdAt ? moment(createdAt).format('LLLL') : 'N/A'}</p>
            <p className='joined-label'>Joined team</p>
          </div>
        </Table.Cell>

        <Table.Cell>
          <div>
            <p className='last-login-date'>{lastLogin ? moment(lastLogin).format('LLLL') : 'N/A'}</p>
            <p className='last-login-label'>Last login</p>
          </div>
        </Table.Cell>
        <Table.Cell>
          {/* <Button className="delete-user-button" icon onClick={() => this.onDeleteItem()}>
            <Icon className="delete-user-icon" name='trash' />
          </Button> */}
          {!this.state.readOnly && (
            <Dropdown icon='ellipsis vertical' floating labeled className='icon'>
              <Dropdown.Menu direction='left'>
                {this.renderResetPasswordMenuItem()}
                <Dropdown.Item icon='trash alternate' text='Delete user' onClick={() => this.onDeleteItem()} />
              </Dropdown.Menu>
            </Dropdown>
          )}
        </Table.Cell>
        <Table.Cell width='1'>{this.state.isBusy && <Loader size='mini'>Loading</Loader>}</Table.Cell>
        {isDeleting && (
          <Confirm
            open={isDeleting}
            mountNode={mountNode}
            header='Delete team member'
            content={deleteContent}
            cancelButton='Never mind'
            confirmButton="Let's do it"
            onCancel={this.onCancelDelete}
            onConfirm={this.onConfirmDelete}
          />
        )}
        {isResetPasswordDialogOpen && (
          <Modal
            as={Form}
            mountNode={mountNode}
            open={true}
            onSubmit={(e: any) => this.onConfirmResetPassword(e)}
            size='small'>
            <Header icon='pencil' content='Change password' as='h4' />
            <Modal.Content>
              <Form.Input
                label='Password'
                required
                type='password'
                placeholder='New password'
                onChange={(_, data) => this.setState({ newPassword: data.value })}
              />
              <Form.Input
                label='Confirm password'
                required
                type='password'
                placeholder='Confirm password'
                onChange={(_, data) => this.setState({ confirmNewPassword: data.value })}
              />
              {resetPasswordError && (
                <Message negative>
                  <Message.Header>Failed to change password</Message.Header>
                  <p>{resetPasswordError}</p>
                </Message>
              )}
            </Modal.Content>
            <Modal.Actions>
              <Button
                type='button'
                color='red'
                icon='times'
                content='Cancel'
                onClick={() => {
                  this.onCloseResetPassword();
                }}
              />
              <Button
                type='submit'
                loading={isResettingPassword}
                color='blue'
                icon='save'
                content='Save'
                disabled={!canResetPassword || isResettingPassword}
              />
            </Modal.Actions>
          </Modal>
        )}
      </Table.Row>
    );
  }
}

const mapStateToProps = (
  // @ts-ignore
  { items, roles, error, currentUserRoleIds, isResetPasswordDialogOpen, isResettingPassword, resetPasswordError, context },
  // @ts-ignore
  { idx },
) => ({
  ...items[idx],
  roles,
  error: error.roles,
  isResetPasswordDialogOpen,
  currentUserRoleIds,
  isResettingPassword,
  resetPasswordError,
  context,
});

const mapDispatchToProps = (dispatch: Dispatch) =>
