// @ts-ignore
export function Error({ errorComponent, error }: {
  errorComponent?: any,
  error: {
    metadata?: string,
    message?: string
  } | string
}): JSX.Element {
  const ErrorComponent = errorComponent;

  return ErrorComponent && ErrorComponent.as !== null ? (
    <ErrorComponent.as
      imgUrl={ErrorComponent.props.imgUrl}
      description={ErrorComponent.props.description}
      error={error}
    />
  ) : (
    <ErrorDefault imgUrl={errorImg} description='Oops, data is not available...' error={error}/>
  );
}
import React from 'react';
import { connect } from 'react-redux';
import UserTableRow from './users-table-row';
import { Table, Visibility, Loader, Dimmer } from 'semantic-ui-react';
import Pagination from './users-pagination';
import { PaginationMode, ErrorComponent } from '../types';
import { bindActionCreators, Dispatch } from 'redux';
import { fetchNextPageAction, fetchMoreItemsActions, UserItemInterface } from './users-reducer';
import { Error } from '../error/error';

export interface UsersTableProps {
  getNextPage: Function;
  getMoreItems: Function;
  loading: boolean;
  loadingRoles: boolean;
  roles: any[];
  paginationMode: PaginationMode;
  errorComponent: ErrorComponent;
  allowChangingPasswords?: boolean;
  error?: {};
}

type MapStateToPropsType = ReturnType<typeof mapStateToProps>;

type DispatchPropsType = ReturnType<typeof mapDispatchToProps>;

class UsersTable extends React.Component<UsersTableProps & MapStateToPropsType & DispatchPropsType, {}> {
  state = {
    page: 0,
  };

  fetchMoreItems = () => {
    this.props.getMoreItems && this.props.getMoreItems();
  };

  fetchNextPage = (activePage: number) => {
    this.props.getNextPage && this.props.getNextPage(activePage);
  };

  isLoading = () => {
    return this.props.loading || this.props.loadingRoles;
  }

  render() {
    const { items, paginationMode, error, errorComponent, allowChangingPasswords, isBusy, loadingRoles } = this.props;
    return error ? (
      <Error error={error} errorComponent={errorComponent} />
    ) : (
        <div>
          {isBusy && <Dimmer active inverted inline="centered">
            <Loader size="mini" />
          </Dimmer>}

          <Table singleLine className='users-table'>
            {!loadingRoles && (
              <Visibility
                as='tbody'
                continuous={false}
                once={false}
                onBottomVisible={() => (paginationMode !== 'pagination' ? this.fetchMoreItems() : null)}>
                {items.map((i: UserItemInterface, idx: number) => (
                  <UserTableRow idx={idx} key={i.id} allowChangingPasswords={allowChangingPasswords} />
                ))}
              </Visibility>
            )}
          </Table>
          {this.isLoading() && paginationMode === 'pagination' && (
            <div style={{ position: 'absolute', left: '50%', marginLeft: '-7px' }}>
              <Loader size='mini' active inline='centered' />
            </div>
          )}
          {paginationMode === 'pagination' && (
            <div style={{ textAlign: 'center', marginBottom: '10px', marginTop: '20px' }}>
              {/*
            // @ts-ignore */}
              <Pagination
                pages={this.props.totalPages}
                page={this.state.page}
                onPageChange={(activePage: number) => {
                  this.fetchNextPage(activePage);
                  this.setState({
                    page: activePage,
                  });
                }}
              />
            </div>
          )}
          {this.isLoading() && paginationMode !== 'pagination' && (
            <div style={{ marginBottom: '5px' }}>
              <Loader size='mini' active inline='centered' />
            </div>
          )}
        </div>
      );
  }
}

// @ts-ignore
const mapStateToProps = ({ loading, items, totalPages, roles, error, isBusy, loadingRoles }) => ({
  loading,
  items,
  isBusy,
  loadingRoles,
  totalPages,
  roles,
  error: error.items,
});
const mapDispatchToProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      getNextPage: fetchNextPageAction,
      getMoreItems: fetchMoreItemsActions,
    },
    dispatch,
  );

export default connect<MapStateToPropsType, DispatchPropsType, UsersTableProps>(
  // @ts-ignore
  mapStateToProps,
  mapDispatchToProps,
)(UsersTable);
import React from 'react';
import { connect } from 'react-redux';
import { Input, Form, Button, Icon, Modal, Header, Label, Dropdown } from 'semantic-ui-react';
