import { searchUserAction, inviteAdminAction } from './users-reducer';
export type TeamInvitationMode = 'emailOnly' | 'popupDialog';

interface DispatchPropsType {
  onSearchUsers(admin: string): Function;
  onInviteAdmin(admin: string): Function;
}

export interface UsersToolbarProps extends DispatchPropsType {
  invitationMode: TeamInvitationMode;
}

type MapStateType = ReturnType<typeof mapStateToProps>;

class UsersToolbar extends React.Component<UsersToolbarProps & MapStateType, {}> {
  private timeout: NodeJS.Timeout | undefined;

  state = {
    email: '',
    name: '',
    phone: '',
    searchFilter: '',
    isAddDialogOpen: false,
    roles: [],
  };

  onSearchChanged = (e: React.ChangeEvent<HTMLInputElement>) => {
    this.setState({ searchFilter: e.target.value });

    if (this.timeout) {
      clearTimeout(this.timeout);
      this.timeout = undefined;
    }

    this.timeout = setTimeout(() => {
      this.props.onSearchUsers && this.props.onSearchUsers(this.state.searchFilter);
    }, 300);
  };

  // @ts-ignore
  onSubmitAdmin = (e) => {
    e.preventDefault();
    const admin: any = {
      email: this.state.email,
      name: this.state.name || this.state.email,
    };

    if (this.state.roles && this.state.roles.length) {
      admin.roleIds = this.state.roles;
    }

    if (this.state.phone) {
      admin.phone = this.state.phone;
    }

    this.props.onInviteAdmin && this.props.onInviteAdmin(admin);
    this.setState({ email: '', name: '', phone: null, isAddDialogOpen: false });
  };

  onOpenAddDialog = () => {
    this.setState({ isAddDialogOpen: true });
    setTimeout(() => {
      if (this.teamMateEmailInput) {
        this.teamMateEmailInput.focus();
      }
    }, 300);
  };
  teamMateEmailInput?: Input;

  render() {
    const roleOptions = this.props.roles.map((role: any) => {
      return {
        key: role.id,
        text: role.name,
        value: role.id,
      };
    });

    const mountNode = document.querySelector('.frontegg .users');

    return (
      <div className='users-toolbar'>
        <Input className='users-search' icon='search' placeholder='Search...' onChange={this.onSearchChanged} />
        {this.props.invitationMode === 'popupDialog' ? (
          <Button onClick={this.onOpenAddDialog} className='add-teammate-button' icon labelPosition='left'>
            <Icon name='add user' />
            Add teammate
          </Button>
        ) : (
          <form className='users-invite-form' onSubmit={this.onSubmitAdmin}>
            <Input
              className='users-invite'
              icon={
                <Button className='users-submit-icon' type='submit'>
                  <Icon name='add user' />
                </Button>
              }
              label='Invite'
              type='email'
              required
              placeholder='Your teammate email address'
              value={this.state.email}
              onChange={(e) => this.setState({ email: e.target.value })}
            />
          </form>
        )}
        {this.state.isAddDialogOpen && (
          <Modal
            open={true}
            mountNode={mountNode}
            // onClose={() => this.setState({ isAddDialogOpen: false })}
            closeOnDimmerClick={true}
            closeOnEscape={true}
            centered={false}>
            <Modal.Header>Add Teammate</Modal.Header>
            <Modal.Content>
              <Form className='add-teammate-form' onSubmit={this.onSubmitAdmin}>
                <Form.Field>
                  <label>Email</label>
                  <Input
                    ref={(i) => i && (this.teamMateEmailInput = i)}
                    icon='at'
                    iconPosition='left'
                    type='email'
                    placeholder='Teammate email'
                    required
                    onChange={(e) => this.setState({ email: e.target.value })}
                  />
                </Form.Field>
                <Form.Field>
                  <label>Name</label>
                  <Input
                    icon='universal access'
                    iconPosition='left'
                    type='text'
                    placeholder='Teammate name'
                    required
                    onChange={(e) => this.setState({ name: e.target.value })}
                  />
                </Form.Field>
                <Form.Field>
                  <label>Role</label>
                  <Dropdown
                    placeholder='Teammate Role'
                    onChange={(_, data) => this.setState({ roles: data.value })}
                    fluid
                    multiple
                    search
                    selection
                    options={roleOptions}
                  />
                </Form.Field>
                <div className='buttons-group'>
                  <Button type='cancel' color='red' onClick={() => this.setState({ isAddDialogOpen: false })}>
                    Cancel
                  </Button>
                  <Button type='submit' color='blue'>
                    Submit
                  </Button>
                </div>
              </Form>
            </Modal.Content>
          </Modal>
        )}
      </div>
    );
  }
}

