import { takeEvery, select, takeLatest, put } from 'redux-saga/effects';
import { Get, Post, Put, Delete } from '../../helpers/fetch';
import Logger from '../../helpers/logger';
import {
  loadItemsAction,
  loadStatsAction,
  editUserAction,
  deleteUserAction,
  resetPasswordAction,
  resendActivationEmailAction,
  searchUserAction,
  inviteAdminAction,
  loadItemFiledAction,
  fetchNextPageAction,
  fetchMoreItemsActions,
  loadStatsSuccessAction,
  loadItemsSuccessAction,
  getAvailableRolesAction,
  inviteAdminSuccessAction,
  availableRolesSuccessAction,
  toggleResetPasswordAction,
  InviteAdminActionProps,
  EditUserActionProps,
  UserItemInterface,
  ResetPasswordActionProps,
  ResendActivationEmailActionProps,
  setIsBusyAction,
} from './users-reducer';

function* loadItemsFunction(added = true) {
  const { pageOffset, pageSize, searchFilter, context } = yield select();
  try {
    const { items, totalPages } = yield Get(context, `/team`, {
      pageOffset,
      pageSize,
      searchFilter,
    });
    const { items: prevItems } = yield select();
    const currentItems = added ? [...prevItems, ...items] : items;
    yield put(loadItemsSuccessAction({ items: currentItems, totalPages }));
  } catch (e) {
    const errorMessage = {
      name: 'items',
      error: e,
    };
    Logger.error('failed to load metadata - ', e);
    yield put(loadItemFiledAction(errorMessage));
  }
}

function* getAvailableRolesFunction() {
  const { context } = yield select();
  try {
    const roles = yield Get(context, '/team/roles', {});
    const { roleIds: currentUserRoleIds } = yield Get(context, '/team/resources/profile/me/v1', { addRoles: true }).catch(() => { return {} });
    yield put(availableRolesSuccessAction({ roles, currentUserRoleIds }));
  } catch (e) {
    const errorMessage = {
      name: 'roles',
      error: e,
    };
    Logger.error('failed to load metadata - ', e);
    yield put(loadItemFiledAction(errorMessage));
  }
}

function* loadStatsFunction() {
  const { context } = yield select();
  try {
    const { totalItems, addedThisWeek } = yield Get(context, '/team/stats', {});
    yield put(loadStatsSuccessAction({ totalItems, addedThisWeek }));
  } catch (e) {
    const errorMessage = {
      name: 'stats',
      error: e,
    };
    Logger.error('failed to load metadata - ', e);
    yield put(loadItemFiledAction(errorMessage));
  }
}

function* inviteAdminFunction({ payload }: InviteAdminActionProps) {
  const { context } = yield select();

  yield put(setIsBusyAction(true))
  try {
    const { item } = yield Post(context, `/team`, payload);
    if (!item.roleIds) {
      item.roleIds = [];
    }
    const { items } = yield select();
    yield put(inviteAdminSuccessAction(item));
  } catch (e) {
    Logger.error('failed to load metadata - ', e);
  }

  yield put(setIsBusyAction(false));
  yield put(loadStatsAction());
}

function* replaceItem(item: any) {
  const { items, totalPages } = yield select();
  const foundItemIndex = items.findIndex((element: UserItemInterface) => element.id === item.id);

  if (foundItemIndex >= 0) {
    const newItems = [...items.slice(0, foundItemIndex), item, ...items.slice(foundItemIndex + 1)];
    yield put(loadItemsSuccessAction({ items: newItems, totalPages }));
  }
}

type actionType = 'edit' | 'delete';

function* editUserFunction(action: actionType, { payload }: EditUserActionProps) {
  const { id } = payload;
  const { context } = yield select();

  yield put(setIsBusyAction(true))

  try {
    yield replaceItem(payload);
    if (action === 'edit') {
      const editedItem = yield Put(context, '/team', payload, { id });
      yield replaceItem(editedItem);
    } else {
      yield Delete(context, `/team`, { id });
      const { items, totalPages } = yield select();
      const reduced = items.filter((item: UserItemInterface) => {
        return item.id != id;
      });
      yield put(loadItemsSuccessAction({ items: reduced, totalPages }));
    }
  } catch (e) {
    Logger.error('failed to load metadata - ', e);
  }

  yield put(setIsBusyAction(false))
}

function* resetPasswordFunction({ payload }: ResetPasswordActionProps) {
  const { id, newPassword } = payload;
  const { context } = yield select();

  yield put(
    toggleResetPasswordAction({
      isResetPasswordDialogOpen: true,
      isResettingPassword: true,
      resetPasswordError: '',
    }),
  );

  try {
    yield Post(context, `/team/resetPassword`, { newPassword }, { userId: id });
  } catch (e) {
    yield put(
      toggleResetPasswordAction({
        isResetPasswordDialogOpen: true,
        isResettingPassword: false,
        resetPasswordError: e.response && e.response.data ? e.response.data.message : 'Failed to reset password',
      }),
    );
    return;
  }

  yield put(toggleResetPasswordAction({ isResetPasswordDialogOpen: false, isResettingPassword: false }));
}

function* resendActivationEmailFunction({ payload }: ResendActivationEmailActionProps) {
  const { id } = payload;
  const { context } = yield select();

  yield Post(context, `/team/resendActivationEmail`, {}, { userId: id });
}

export function* rootSaga() {
  yield takeLatest([loadItemsAction, searchUserAction, fetchMoreItemsActions], loadItemsFunction);
  yield takeLatest(fetchNextPageAction, () => loadItemsFunction(false));
  yield takeEvery(getAvailableRolesAction, getAvailableRolesFunction);
  yield takeEvery([loadStatsAction, loadItemsSuccessAction], loadStatsFunction);
  yield takeLatest(inviteAdminAction, inviteAdminFunction);
  yield takeLatest(editUserAction, (props: EditUserActionProps) => editUserFunction('edit', props));
  yield takeLatest(deleteUserAction, (props) => editUserFunction('delete', props));
  yield takeLatest(resetPasswordAction, (props) => resetPasswordFunction(props));
  yield takeLatest(resendActivationEmailAction, (props) => resendActivationEmailFunction(props));
}
import React from 'react';
import { Provider } from 'react-redux';
import createSagaMiddleware, { Task } from 'redux-saga';
import { composeWithDevTools } from 'redux-devtools-extension/developmentOnly';
import { createStore, applyMiddleware } from 'redux';
