interface ICommonContainer {
  reducer: any;
  initialState: any;
  rootSaga: any;
  children: React.ReactElement;
}

const sagaMiddleware = createSagaMiddleware();
const storeMiddleware = process.env.NODE_ENV === 'development' ?
  composeWithDevTools(applyMiddleware(sagaMiddleware)) :
  applyMiddleware(sagaMiddleware);

export default class CommonContainer extends React.Component<ICommonContainer> {
  store: any;
  task: Task;

  constructor(props: ICommonContainer) {
    super(props);
    const { reducer, initialState, rootSaga } = this.props;
    this.store = createStore(reducer, initialState, storeMiddleware);
    this.task = sagaMiddleware.run(rootSaga);
  }

  componentWillUnmount() {
    this.task.cancel();
  }

  render() {
    const { children } = this.props;
    return (
      <Provider store={this.store}>
        <ContextConsumer>
          {(context) => <div className='frontegg'>{React.cloneElement(children, { context })}</div>}
        </ContextConsumer>
      </Provider>
    );
  }
}
