}

export const initialState: AuditsState = {
  total: 0,
  offset: 0,
  filter: '',
  sortBy: 'createdAt',
  context: null,
  filters: [],
  rowsData: [],
  isLoading: true,
  isFetchMore: false,
  totalToday: 0,
  currentPage: 0,
  headerProps: [],
  sortDirection: 'desc',
  severeThisWeek: 0,
  predefinedFilters: [],
};

export const defaultItemsPerPage = 20;

const { name, actions, reducer } = createSlice({
  name: 'audits',
  initialState,
  reducers: {
    setContext: (state, { payload }) => {
      state.context = payload;
    },
    startLoading: (state) => {
      state.isLoading = true;
    },
    startFetching: (state) => {
      state.isFetchMore = true;
    },
    startReloading: (state) => {
      state.isLoading = true;
    },
    finishLoading: (state) => {
      state.isLoading = false;
    },
    loadStatsSuccess: (state, { payload }) => {
      state.error = undefined;
      state.totalToday = payload.totalToday;
      state.severeThisWeek = payload.severeThisWeek;
    },
    loadItemFiledAction: (state, { payload }) => {
      state.error = { ...state.error, [payload.name]: payload.error };
    },
    loadMetadataSuccess: (state, { payload }) => {
      state.error = {};
      state.headerProps = payload.properties;
      state.themeAudits = payload.theme ? payload.theme.styles : {};
    },
    loadAuditsSuccess: (state, { payload }) => {
      state.error = {};
      state.rowsData = payload.rowsData;
      state.total = payload.total;
    },
    fetchMoreSuccess: (state, { payload }) => {
      state.error = {};
      state.offset = payload.offset;
      state.currentPage = payload.currentPage;
      state.isFetchMore = false;
    },
    setFilterData: (state, { payload }) => {
      state.filters = payload;
      state.currentPage = 0;
      state.offset = 0;
      state.isLoading = true;
    },
    textSearch: (state, { payload }) => {
      state.filter = payload;
      state.currentPage = 0;
      state.offset = 0;
      state.isLoading = true;
    },
    onPageChange: (state, { payload }) => {
      state.currentPage = payload - 1;
      state.offset = state.currentPage * defaultItemsPerPage;
      state.isLoading = true;
    },
    setDataSorting: (state, { payload }) => {
      state.sortBy = payload.sortBy;
      state.currentPage = 0;
      state.sortDirection = payload.direction === 'asc' ? 'desc' : 'asc';
      state.offset = 0;
      state.isLoading = true;
    },
    setPredefinedFilters: (state, { payload }) => {
      state.predefinedFilters = payload;
      state.filters = Object.keys(payload).map((key) => ({ key, value: payload[key] }));
    },
  },
});

