class Search extends React.Component<DispatchProps & StateToProps, {}> {
  private timeout: NodeJS.Timeout;

  state = {
    searchTerm: '',
    showPanel: false,
    filterEnabled: false,
    filterName: '',
    filterText: '',
  };

  toggleFilter(filterKey, filterEnabled) {
    this.setState({
      filterName: filterKey,
      filterText: '',
      filterEnabled,
    });
  }

  createFilterObject(e) {
    e.preventDefault();

    const { filterText, filterName } = this.state;
    let obj;
    let value;
    if (filterText) {
      if (filterName === 'createdAt') {
        switch (filterText) {
          case 'last_day':
            value = moment().utc().subtract(1, 'days').toString();
            break;
          case 'last_week':
            value = moment().utc().subtract(7, 'days').toISOString();
            break;
          case 'last_month':
            value = moment().utc().subtract(30, 'days').toISOString();
            break;
          case 'last_year':
            value = moment().utc().subtract(365, 'days').toISOString();
            break;
          default:
            value = moment().utc().toISOString();
        }
        obj = {
          key: filterName,
          value: { $gt: value },
        };
      } else {
        obj = {
          key: filterName,
          value: filterText,
        };
      }
      this.props.filterData(obj);
    }
    this.setState({ filterText: '' });
    this.toggleFilter('', false);
  }

  getTimeValue(time) {
    switch (moment().diff(time, 'days')) {
      case 1:
        return 'Last Day';
      case 7:
        return 'Last Week';
      case 30:
        return 'Last Month';
      case 365:
        return 'Last Year';
      default:
        return time;
    }
  }

  private findFilterName(key) {
    const { metadata } = this.props;
    const item = metadata && metadata.find((i) => i.name === key);

    return item ? item.displayName : key;
  }

  onSearchChanged(e) {
    this.setState({ searchTerm: e.target.value });

    if (this.timeout) {
      clearTimeout(this.timeout);
      this.timeout = null;
    }

    this.timeout = setTimeout(() => {
      this.props.textSearch && this.props.textSearch(this.state.searchTerm);
    }, 300);
  }

  render() {
    const { searchTerm, showPanel, filterEnabled } = this.state;
    const { filterName, filterText } = this.state;

    return (
      <div>
        <div className='fe-audits-separator fe-audits-search-div'>
          <div className='fe-audits-search-form-div'>
            <form
              className='fe-audits-search-form'
              onSubmit={(e) => {
                e.preventDefault();
                this.props.textSearch(searchTerm);
              }}>
              <input
                type='text'
                value={searchTerm}
                onChange={(e) => this.onSearchChanged(e)}
                placeholder='Search by any text'
                className='fe-audits-search-input fe-audits-search-box-content fe-audits-search-box'
              />
            </form>
            <div
              onClick={() => this.setState({ showPanel: !showPanel })}
              className={
                this.props.filters.length !== 0
                  ? 'fe-audits-filter-tags fe-audits-dis-sm-block fe-audits-filter-tags-visible'
                  : 'fe-audits-filter-tags fe-audits-dis-sm-block'
              }>
              <span>
                <img src={filterIcon} alt='filter' width={25} height={25} />
              </span>
              <span
                className={
                  this.props.filters.length !== 0
                    ? 'fe-audits-filter-marker-change fe-audits-filter-marker'
                    : 'fe-audits-filter-marker'
                }>
                <p>{this.props.filters.length}</p>
              </span>
            </div>
          </div>
          {this.props.filters.length !== 0 && (
            <div className='fe-audits-filter-text-container'>
              <div
                onClick={() => this.setState({ showPanel: !showPanel })}
                className={
                  this.props.filters.length !== 0
                    ? 'fe-audits-filter-tags fe-audits-dis-sm-none fe-audits-filter-tags-visible'
                    : 'fe-audits-filter-tags fe-audits-dis-sm-none'
                }>
                <span>
                  <img src={filterIcon} alt='filter' width={25} height={25} />
                </span>
                <span
                  className={
                    this.props.filters.length !== 0
                      ? 'fe-audits-filter-marker-change fe-audits-filter-marker'
                      : 'fe-audits-filter-marker'
                  }>
                  {this.props.filters.length}
                </span>
              </div>
              <div
                className={`fe-audits-search-box-content fe-audits-hide-box fe-audits-display-box ${
                  this.props.filters.length === 0 && 'd-none'
                  }`}
                style={{ minHeight: '2rem' }}>
                <ul className='fe-audits-search-box-ul'>
                  {this.props.filters.length !== 0
                    ? this.props.filters.map((filter, index) => (
                      <li key={index} className='fe-audits-search-li-active'>
                        <span className='fe-audits-filter-text'>{`${this.findFilterName(filter.key)}: ${
                          filter.key === 'createdAt' ? this.getTimeValue(filter.value.$gt) : filter.value
                          }`}</span>
                        <span className='fe-audits-cancel-icon' onClick={() => this.props.removeFilter(filter)}>
                          ⓧ
                          </span>
                      </li>
                    ))
                    : null}
                </ul>
              </div>
            </div>
          )}

          <div className='fe-audits-hide-box'>
            <ul className='fe-audits-search-icon-ul'>
              {/* <li onClick={() => this.props.deleteAudits()}>
                <img src={deleteIcon} alt="deleteIcon" className="fe-audits-action-icon" />
              </li> */}
              {/* <li>
                <img
                  src={printIcon}
                  alt="printIcon"
                  className="fe-audits-action-icon"
                />
              </li> */}
              <li className="fe-export-pdf" onClick={() => this.props.exportPDF()}>
                <ExportPdfIcon className='fe-audits-action-icon' />
              </li>
              <li className="fe-export-csv" onClick={() => this.props.exportCSV()}>
                <ExportCsvIcon className='fe-audits-action-icon' />
              </li>
            </ul>
          </div>
        </div>
        {showPanel && (
          <div className='fe-audits-search-filter-div'>
            <h5 className='fe-audits-search-user-text'>
              Filter By{' '}
              {filterEnabled
                ? filterName === 'createdAt'
                  ? 'Time'
                  : filterName.charAt(0).toUpperCase() + filterName.slice(1)
                : 'Options'}
              <span
                className='fe-audits-cancel-filter-btn'
                onClick={() => (filterEnabled ? this.toggleFilter('', false) : this.setState({ showPanel: false }))}>
                X
              </span>
            </h5>
            {!filterEnabled ? (
              <div>
                <ul className='fe-audits-search-filters-ul'>
                  <li onClick={() => this.toggleFilter('createdAt', true)}>
                    <span>Time</span>
                    <span className='fe-audits-search-icon'>{'>'}</span>
                  </li>
                  <li onClick={() => this.toggleFilter('user', true)}>
                    <span>User</span>
                    <span className='fe-audits-search-icon'>{'>'}</span>
                  </li>
                  <li onClick={() => this.toggleFilter('resource', true)}>
                    <span>Resource</span>
                    <span className='fe-audits-search-icon'>{'>'}</span>
                  </li>
                </ul>
              </div>
            ) : (
                <form onSubmit={(e) => this.createFilterObject(e)}>
                  <div className='fe-audits-search-user-input-div'>
                    {filterName === 'createdAt' ? (
                      <select
                        className='fe-audits-filter-select'
                        value={filterText || 'select'}
                        onChange={(e) => this.setState({ filterText: e.target.value })}>
                        <option disabled value='select'>
                          Select Time
                      </option>
                        <option value='last_day'>Last Day</option>
                        <option value='last_week'>Last Week</option>
                        <option value='last_month'>Last Month</option>
                        <option value='last_year'>Last Year</option>
                      </select>
                    ) : (
                        <input
                          type={'text'}
                          placeholder='Search by text...'
                          className='fe-audits-search-user-input'
                          value={filterText}
                          onChange={(e) => this.setState({ filterText: e.target.value })}
                        />
                      )}
                    <div className='fe-audits-confirm-btn' onClick={(e) => this.createFilterObject(e)}>
                      <ConfirmIcon />
                    </div>
                  </div>
                </form>
              )}
          </div>
        )}
      </div>
    );
  }
}

