import { HeaderProps } from '../../reducer';

import lucene, { BinaryAST, Node } from 'lucene';

type TValidate = boolean;

export const showInMoreInfoValidate = (col: HeaderProps, row: Record<string, string>): TValidate => {
  const { left } = lucene.parse(col.showInMoreInfo) as BinaryAST;
  const { field, term } = left as Node;

  if (row) {
    //@ts-ignore
    if (!!row[col.name] || row[col.name] === 0) {
      return term.toLowerCase() === `${row[field]}`.toLowerCase() || term.toLowerCase() === 'always';
    }
  }
  return false;
};

export const severityValidate = (col: HeaderProps, row: Record<string, string>) => {
  return (`${col.name}`.toLowerCase() === 'severity' && `severity__${`${row[col.name]}`.toLowerCase()}`) || '';
};
import { RefObject } from 'react';

export function getOffsetRect(ref: RefObject<any> | null, width: number) {
  if (ref && ref.current) {
    const container = document.querySelector('.frontegg .fe-audits-container')!.getBoundingClientRect();
    const box = ref.current.getBoundingClientRect();

    let top = box.top - container.top;
    let left = box.left - container.left;
    if (left + width > container.width) {
      left = left - width;
      top = top;
    }
    return {
      top: Math.round(top + box.height + 5),
      left: Math.round(left),
    };
  }
}
