const pageSize = 2;

export default class TableJsonComponent extends React.Component<TableJsonComponentProps, {}> {
  private node: React.LegacyRef<HTMLDivElement> | any;
  private jsonIconRef = React.createRef<HTMLImageElement>();
  state = {
    showPop: false,
    activePage: 1,
    totalPages: Math.ceil(this.props.json.length / 2),
    items: this.props.json.slice(0, pageSize),
    mountNode: null,
  };

  async componentDidMount() {
    const portalNode = document.querySelector('.frontegg .fe-audits-container');
    portalNode && this.setState({ mountNode: portalNode });
  }

  onToggleShowJson = () => {
    this.setState({ showPop: !this.state.showPop });
  };

  onPageChange = (pageNumber?: number) => {
    pageNumber &&
      this.setState({
        items: this.props.json.slice((pageNumber - 1) * pageSize, pageNumber * pageSize),
        activePage: pageNumber,
      });
  };

  render() {
    const { json } = this.props;
    const { showPop, items, activePage, totalPages } = this.state;
    const borderBottom = (items.length > 1 && 'borderBottom') || '';
    const showingItems = items.length >= 2 ? activePage * pageSize : activePage * pageSize - 1;
    const showPagination = json.length > pageSize;
    return (
      <>
        {json.length} total
        <img
          className='jsonMenu__icon'
          id='jsonMenuIcon'
          ref={this.jsonIconRef}
          onClick={this.onToggleShowJson}
          src={jsonMenuIcon}
        />
        {showPop && (
          <Portal node={this.state.mountNode}>
            <OutsideClickHandler
              onOutsideClick={() => {
                this.setState({ showPop: false });
              }}>
              <div ref={(node) => (this.node = node)} style={getOffsetRect(this.jsonIconRef, 340)} className='jsonMenu'>
                <div className='jsonMenu__info'>
                  {json.length} total (showing {showingItems} out of {json.length})
                </div>
                {items.map((object: any, idx) =>
                  typeof object === 'string' ? (
                    <span key={idx} className={`jsonMenu__itemGroup jsonMenu__itemGroup--string ${borderBottom}`}>
                      <span key={idx} className='jsonMenu__item'>
                        <p className='jsonMenu__name'>{(activePage - 1) * pageSize + (idx + 1)}</p>
                        <p className='jsonMenu__desc'>{object}</p>
                      </span>
                    </span>
                  ) : (
                    <span key={idx} className={`jsonMenu__itemGroup ${borderBottom}`}>
                      {Object.keys(object).map((key) => {
                        return (
                          <span key={key} className='jsonMenu__item'>
                            <p className='jsonMenu__name'>{key}</p>
                            <p className='jsonMenu__desc'>
                              {typeof object[key] === 'object' ? JSON.stringify(object[key]) : object[key]}
                            </p>
                          </span>
                        );
                      })}
                    </span>
                  ),
                )}
                {showPagination && (
                  <Semantic.Pagination
                    className='jsonMenu__pagination'
                    firstItem={null}
                    lastItem={null}
                    pointing
                    secondary
                    onPageChange={(_, data) => {
                      // @ts-ignore
                      this.onPageChange(data.activePage);
                    }}
                    activePage={activePage}
                    totalPages={totalPages}
                  />
                )}
              </div>
            </OutsideClickHandler>
          </Portal>
        )}
      </>
    );
  }
}
