    tableNode?.removeEventListener('scroll', this.closeSubMenuOnScroll);
  }

  async loadIpAddressMetadata() {
    this.setState({ loading: true });

    try {
      //@ts-ignore
      await Get(this.props.context, `/metadata/ip/${this.props.ip}`).then((data) => this.setState({ data }));
    } catch (e) {
      Logger.error('failed to load metadata for ip address - ', e);
    }
    this.setState({ loading: false });
  }

  componentDidUpdate(prevProps: { ip: string }) {
    if (this.state.showSubMenu) {
      document.addEventListener('click', this.onClickOut);
    } else {
      document.removeEventListener('click', this.onClickOut);
    }

    if (this.props.ip !== prevProps.ip) {
      this.loadIpAddressMetadata();
    }
  }

  onClickOut = (event: MouseEvent) => {
    const target = event.target as HTMLDivElement;
    this.node &&
      !this.node.contains(target) &&
      target.id !== 'ipAddress__subMenu' &&
      this.setState({ showSubMenu: false });
  };

  renderItems = (key: string) => {
    const { data } = this.state;
    switch (key) {
      case 'city':
        return (
          <span key={key} className='ipAddress__item'>
            <p className='ipAddress__name'>City</p>
            <p className='ipAddress__desc'>{this.validateData(data[key]) ? data[key] : 'unknown'}</p>
          </span>
        );
      case 'latitude':
        return (
          <span key={key} className='ipAddress__item'>
            <p className='ipAddress__name'>Latitude, Longitude</p>
            <p className='ipAddress__desc'>
              {this.validateData(data.latitude) ? `${data.latitude.toFixed(4)},` : 'unknown'}
              {this.validateData(data.longitude) ? data.longitude.toFixed(4) : ''}
            </p>
          </span>
        );
      case 'country_name':
        return (
          <span key={key} className='ipAddress__item'>
            <p className='ipAddress__name'>Country</p>
            <p className='ipAddress__desc'>{this.validateData(data[key]) ? data[key] : 'unknown'}</p>
          </span>
        );
      case 'zip':
        return (
          <span key={key} className='ipAddress__item'>
            <p className='ipAddress__name'>Zip</p>
            <p className='ipAddress__desc'>{this.validateData(data[key]) ? data[key] : 'unknown'}</p>
          </span>
        );
      default:
        return;
    }
  };

  //@ts-ignore
  validateData = (data) => {
    return data !== null;
  };

  getPopupPosition = () => {
    const cords = getOffsetRect(this.ipRef, 460) || { top: 0 };
    const tableNode = document.querySelector('.frontegg .table-wrapper');
    const { height: tableHeight } = tableNode?.getBoundingClientRect() || { height: 0 };
    if (cords?.top > tableHeight) {
      cords.top = cords.top - 208;
    }
    return cords;
  }

  render() {
    let { ip } = this.props;
    const { data, showSubMenu, loading } = this.state;

    if (ip === '::1') {
      ip = '127.0.0.1';
    }

    return (
      <div className='ipAddress'>
        <div ref={this.ipAddressRef} className='ipAddress__container'>
          <div className='ipAddress__img'>
            {loading ? (
              <Loader className='ipAddress__loader' active inline='centered' size='mini' />
            ) : (
                data.location.country_flag &&
                this.validateData(data.location.country_flag_emoji) && (
                  <div>{data.location.country_flag_emoji}</div>
                )
              )}
          </div>

          <div
            ref={this.ipRef}
            onClick={() => this.validateData(data) && this.setState({ showSubMenu: !showSubMenu })}
            className='ipAddress__ip'>
            {ip}
          </div>

          {showSubMenu && (
            <Portal node={this.state.mountNode}>
              <div
                id='ipAddress__subMenu'
                ref={(node) => (this.node = node)}
                style={this.getPopupPosition()}
                className='ipAddress__subMenu'>
                <span className='ipAddress__itemGroup'>
                  {Object.keys(data).map((key) => {
                    return this.renderItems(key);
                  })}
                </span>
                <span className='ipAddress__map'>
                  {this.validateData(data) && (
                    <GoogleMapReact
                      bootstrapURLKeys={{ key: 'AIzaSyC588_3ox-RV9tt46VXdRkaPpSSZbSDAfE' }}
                      defaultCenter={{
                        lat: this.validateData(data.latitude) ? data.latitude : defaultCenter.lat,
                        lng: this.validateData(data.longitude) ? data.longitude : defaultCenter.lng,
                      }}
                      defaultZoom={this.validateData(data.longitude) ? 11 : 0}>
                      {this.validateData(data.longitude) && (
                        <MapMark
                          //@ts-ignore
                          lat={this.validateData(data.latitude) ? data.latitude : defaultCenter.lat}
                          lng={this.validateData(data.longitude) ? data.longitude : defaultCenter.lng}
                        />
                      )}
                    </GoogleMapReact>
                  )}
                </span>
              </div>
            </Portal>
          )}
        </div>
      </div>
    );
  }
}

