import React from 'react';
import moment from 'moment';

import { connect } from 'react-redux';
import { bindActionCreators, Dispatch } from 'redux';
import Logger from '../../../helpers/logger';
import { ErrorComponent } from '../../types';
import TableSemanticContent from './table-semantic-content';
import { fetchMoreItems, filterData, onPageChange, setDataSorting } from '../reducer';
import { headerPropsWithoutPredefinedFilters } from '../selectors';
import './styles/style.scss';
import { Error } from '../../error/error';
import { Loader } from 'semantic-ui-react';

export interface TableContentProps {
  paginationMode?: string;
  errorComponent?: ErrorComponent;
  isDemonstration?: boolean;
}

class TableContent extends React.Component<TableContentProps & StateToProps & DispatchProps, {}> {
  state = {
    filter: {
      key: '',
      value: '',
      obj: '',
      type: '',
    },
    selectAll: false,
    selection: [],
  };

  escFunction = (event) => {
    if (event.keyCode === 27) {
      this.setState({
        filter: {
          key: '',
          value: '',
          obj: {},
          type: '',
        },
      });
    }
  };

  componentDidMount() {
    document.addEventListener('keydown', this.escFunction, false);
  }

  componentWillUnmount() {
    document.removeEventListener('keydown', this.escFunction, false);
  }

  loadData = () => {
    Logger.log('fetchMoreItems');
    this.props.fetchMoreItems ? this.props.fetchMoreItems() : null;
  };

  handleKeyFilter = (col) => {
    this.setState({
      filter: {
        key: col.name,
        value: '',
        obj: {},
        type: col.type,
      },
    });
  };

  createFilterObj = () => {
    let obj;
    if (this.state.filter.value) {
      if (this.state.filter.type === 'Timestamp') {
        let value;
        switch (this.state.filter.value) {
          case 'last_day':
            value = moment().utc().subtract(1, 'days').toISOString();
            break;
          case 'last_week':
            value = moment().utc().subtract(7, 'days').toISOString();
            break;
          case 'last_month':
            value = moment().utc().subtract(30, 'days').toISOString();
            break;
          case 'last_year':
            value = moment().utc().subtract(365, 'days').toISOString();
            break;
          default:
            value = moment().utc().toISOString();
        }
        obj = {
          key: this.state.filter.key,
          value: { $gt: value },
        };
      } else {
        obj = {
          key: this.state.filter.key,
          value: this.state.filter.value,
        };
      }

      this.props.filterData(obj);
    }
    this.setState({
      filter: {
        key: '',
        value: '',
        obj: '',
        type: '',
      },
    });
  };

  handleFilterValueChange = (e) => {
    const value = e.target.value;
    this.setState((prevState: any) => ({
      filter: { ...prevState.filter, value: value },
    }));
  };

  handleSelect = (e) => {
    const value = e.target.value;
    this.setState((prevState: any) => ({
      filter: { ...prevState.filter, value: value },
    }));
  };

  toggleSelection = (key: string, shift: boolean, row: any) => {
    // start off with the existing state
    let selection = [...this.state.selection];
    const keyIndex = selection.indexOf(key);
    // check to see if the key exists
    if (keyIndex >= 0) {
      // it does exist so we will remove it using destructing
      selection = [...selection.slice(0, keyIndex), ...selection.slice(keyIndex + 1)];
    } else {
      // it does not exist so add it
      selection.push(key);
    }
    // update the state
    this.setState({ selection });
  };

  toggleAll = () => {
    const selectAll = !this.state.selectAll;
    const selection = this.state.selection;

    if (selectAll) {
      const data = this.props.rowsData;
      // we just push all the IDs onto the selection array
      data.forEach((item) => {
        selection.push(item.frontegg_id);
      });
      this.setState({ selectAll, selection });
    } else {
      this.setState({ selectAll, selection: [] });
    }
  };

  isSelected = (key) => {
    return this.state.selection.includes(key);
  };

  render() {
    const {
      rowsData,
      headerProps,
      isFetchMore,
      filterData,
      sortDirection,
      sortBy,
      onPageChange,
      errorComponent,
      setDataSorting,
      fetchMoreItems,
      paginationMode,
      isLoading,
      total,
      error,
    } = this.props;

    return error ? (
      <Error errorComponent={errorComponent} error={error} />
    ) : (
        <div className="fe-audits-table-content">
          {isLoading && <Loader active centered />}
          <TableSemanticContent
            isDemonstration={this.props.isDemonstration}
            rowsData={rowsData}
            headerProps={headerProps}
            filterData={filterData}
            sortDirection={sortDirection}
            sortBy={sortBy}
            onPageChange={onPageChange}
            handleSorting={(sortBy, direction) => setDataSorting({ sortBy, direction })}
            fetchMoreItems={fetchMoreItems}
            paginationMode={paginationMode}
            total={total}
            isLoading={isLoading}
            isFetchMore={isFetchMore}
            toggleAll={this.toggleAll}
            toggleSelection={(frontegg_id, user, action) => this.toggleSelection(frontegg_id, user, action)}
            selectAll={this.state.selectAll}
            handleKeyFilter={(col) => this.handleKeyFilter(col)}
            handleSelect={this.handleSelect}
            createFilterObj={this.createFilterObj}
            handleFilterValueChange={this.handleFilterValueChange}
            filter={this.state.filter}
            selection={this.state.selection}
            loadData={() => this.loadData()}
            isSelected={this.isSelected}
          />
        </div>
      );
  }
}

const mapStateToProps = (state) => {
  return {
    total: state.total,
    sortBy: state.sortBy,
    rowsData: state.rowsData,
    error: state.error?.audits,
    isFetchMore: state.isFetchMore,
    isLoading: state.isLoading,
    sortDirection: state.sortDirection,
    headerProps: headerPropsWithoutPredefinedFilters(state),
  };
};

const mapDispatchProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      filterData,
      onPageChange,
      fetchMoreItems,
      setDataSorting,
    },
    dispatch,
  );

type DispatchProps = ReturnType<typeof mapDispatchProps>;

type StateToProps = ReturnType<typeof mapStateToProps>;

export default connect<StateToProps, DispatchProps, TableContentProps>(mapStateToProps, mapDispatchProps)(TableContent);
import moment from 'moment';

// @ts-ignore
export const getLastUpdatedTime = ({ date, currentDate = moment() }) => {
  const seconds = currentDate.diff(date, 'seconds');
  const minutes = currentDate.diff(date, 'minutes');
  const hours = currentDate.diff(date, 'hours');
  const days = currentDate.diff(date, 'days');
  const months = currentDate.diff(date, 'months');
  if (minutes < 1) {
    return `${seconds} seconds ago`;
  }
  if (minutes < 60) {
    return `${minutes} ${minutes < 2 ? 'minute' : 'minutes'} ago`;
  }
  if (minutes > 60 && hours < 24) {
    return `${hours} ${hours < 2 ? 'hour' : 'hours'} ago`;
  }
  if (hours > 24 && days < 30) {
    return `${days} ${days < 2 ? 'day' : 'days'} ago`;
  }
  return `${months} ${months < 2 ? 'month' : 'months'} ago`;
};
