import React from 'react';
import Table from './Table';
import { connect } from 'react-redux';
import { bindActionCreators, Dispatch } from 'redux';
import { PaginationMode, ErrorComponent } from '../types';
import { ContextOptions } from '../../providers/context';
import ThemeProvider from '../theme/theme-provider';
import { setContext, initData, loadAudits, setFilterData, AuditsState, setPredefinedFilters } from './reducer';
import { Error } from '../error/error';
import { isEqual } from 'lodash';
import { Loader } from 'semantic-ui-react';

interface AuditsContainerProps {
  context?: ContextOptions;
  hideStatistics?: boolean;
  paginationMode?: PaginationMode;
  errorComponent?: ErrorComponent;
  isDemonstration?: boolean;
  pageSize?: number;
  predefinedFilters?: { [key: string]: string };
}

class AuditsContainer extends React.Component<AuditsContainerProps & DispatchProps & StateToProps, {}> {
  async componentDidMount() {
    const { setContext, initData, predefinedFilters, setPredefinedFilters } = this.props;

    await setContext(this.props.context);
    predefinedFilters && setPredefinedFilters(predefinedFilters);
    initData();
  }

  async componentDidUpdate(prevProps: AuditsContainerProps & DispatchProps & StateToProps) {
    const { setContext, initData, setFilterData } = this.props;

    try {
      if (!isEqual(this.props.context, prevProps.context)) {
        setContext(this.props.context);
        setFilterData([]);
        if (this.props.isDemonstration !== true) {
          initData();
        }
      }
    } catch (e) {
      console.error(e);
    }
  }

  render() {
    const theme = this.props.context?.theme || this.props.themeAudits;
    const { isLoading, error, headerProps, errorComponent } = this.props;

    let components;
    if (isLoading && headerProps && headerProps.length === 0) {
      components = <Loader active={true}/>;
    } else if (error) {
      components = <Error errorComponent={errorComponent} error={error}/>;
    } else if (headerProps && headerProps.length > 0) {
      components = <ThemeProvider context={this.props.context} themeMetadata={theme}>
        <Table
          isDemonstration={this.props.isDemonstration}
          hideStatistics={this.props.hideStatistics}
          paginationMode={this.props.paginationMode}
          errorComponent={this.props.errorComponent}
        />
      </ThemeProvider>;
    } else {
      components = <div>No data was found!</div>;
    }

    return components;
  }
}

const mapDispatchProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      setContext,
      loadAudits,
      initData,
      setFilterData,
      setPredefinedFilters,
    },
    dispatch,
  );

const mapStateToProps = ({ themeAudits, headerProps, isLoading, error }: AuditsState) => ({
  themeAudits,
  headerProps,
  isLoading,
  error: error?.metadata,
});

type DispatchProps = ReturnType<typeof mapDispatchProps>;

type StateToProps = Pick<AuditsState, 'themeAudits' | 'headerProps' | 'isLoading'> & { error?: string };

export default connect<StateToProps, DispatchProps, AuditsContainerProps, AuditsState>(
  mapStateToProps,
  mapDispatchProps,
)(AuditsContainer);
import { takeLatest, select, put, all, takeEvery, call } from 'redux-saga/effects';
import Logger from '../../helpers/logger';
import { Get, Download } from '../../helpers/fetch';
import {
  Filter,
  initData,
  exportCSV,
  exportPDF,
  loadAudits,
  textSearch,
  filterData,
  startLoading,
  startReloading,
  removeFilter,
  onPageChange,
  finishLoading,
  setFilterData,
  fetchMoreItems,
  setDataSorting,
  loadStatsSuccess,
  loadAuditsSuccess,
  fetchMoreSuccess,
  loadItemFiledAction,
  loadMetadataSuccess,
  defaultItemsPerPage,
  LoadAuditsProps,
  RemoveFilterProps,
  FilterDataProps,
  startFetching,
} from './reducer';

function* loadStats() {
  const { sortBy, sortDirection, context } = yield select();
  try {
    const stats = yield Get(context, '/audits/stats', {
      sortBy,
      sortDirection,
      count: defaultItemsPerPage,
    });
    yield put(loadStatsSuccess(stats));
  } catch (e) {
    const errorMessage = {
      name: 'stats',
      error: e,
    };
    Logger.error('failed to load stats - ', e);
    yield put(loadItemFiledAction(errorMessage));
  }
}

function* loadMetadata() {
  const { context } = yield select();
  try {
    const result = yield Get(context, '/metadata', { entityName: 'audits' });
    yield put(loadMetadataSuccess(result.rows[0]));
    Logger.debug(result);
  } catch (e) {
    const errorMessage = {
      name: 'metadata',
      error: e,
    };
    Logger.error('failed to load metadata - ', e);
    yield put(loadItemFiledAction(errorMessage));
  }
}

const filterToObject = (arr: Array<Filter>) =>
  arr.reduce((res: Record<string, string>, curr) => {
    res[curr.key] = curr.value;
    return res;
  }, {});

function* loadAuditsFunction({ payload }: LoadAuditsProps) {
  const { appendMode = false, onlyOneLoad = true, offset: incomeOffset } = payload || {};
  const { context, filters, sortBy, sortDirection, filter, offset } = yield select();
  Logger.debug('Loaded audits', { offset, incomeOffset, count: defaultItemsPerPage });
  try {
    const f2o = filterToObject(filters);

    const { data, total } = yield Get(context, '/audits', {
      sortDirection,
      sortBy,
      filter,
      ...f2o,
      offset: incomeOffset || offset,
      count: defaultItemsPerPage,
    });

    Logger.debug('Loaded audits', { data });
    const { rowsData } = appendMode ? yield select() : { rowsData: [] };
    // Logger.debug(`Going to update the state`);
    yield put(loadAuditsSuccess({ rowsData: [...rowsData, ...data], total }));
    // Logger.debug(`Done. going to finish loading`);
    onlyOneLoad && (yield put(finishLoading()));
  } catch (e) {
    const errorMessage = {
      name: 'audits',
      error: e,
    };
    // Logger.error('failed to load audits - ', e);
    yield put(loadItemFiledAction(errorMessage));
  }
}

function* initDataFunction() {
  yield put(startLoading());
  yield all([loadStats(), loadMetadata(), loadAuditsFunction({ payload: { onlyOneLoad: false }, type: '' })]);
  yield put(finishLoading());
}

function* fetchMoreItemsFunction() {
  const { currentPage, total, isFetchMore } = yield select();
  if (isFetchMore) {
    return;
  }
  const maxPage = Math.ceil(total / defaultItemsPerPage);
  const newPage = currentPage + 1;
  if (newPage > maxPage) {
    return;
  }
  const newOffset = newPage * defaultItemsPerPage;
  Logger.debug('fetchMoreItemsFunction', { currentPage, total, newPage, newOffset });

  yield put(startFetching());
  yield loadAuditsFunction({ payload: { offset: newOffset, appendMode: true }, type: '' });
  yield put(fetchMoreSuccess({ offset: newOffset, currentPage: newPage }));
}

function* removeFilterFunction({ payload }: RemoveFilterProps) {
  const { filters: allFilters } = yield select();
  const removedFilterIndex = allFilters.findIndex((item: Filter) => item.key == payload.key);

  if (removedFilterIndex < 0) {
    return;
  }

  const newFilters = [...allFilters.slice(0, removedFilterIndex), ...allFilters.slice(removedFilterIndex + 1)];
  yield put(setFilterData(newFilters));
}

function* filterDataFunction({ payload }: FilterDataProps) {
  const { filters: allFilters } = yield select();
  let filterIndex = allFilters.findIndex((item: Filter) => item.key == payload.key);

  if (filterIndex < 0) {
    filterIndex = allFilters.length;
  }

  const newFilters = [...allFilters.slice(0, filterIndex), payload, ...allFilters.slice(filterIndex + 1)];
  yield put(setFilterData(newFilters));
}

function* exportAuditsFunction(format: string) {
  const { context, filters, sortBy, sortDirection, filter, headerProps } = yield select();
  try {
    const blob = yield Download(
      context,
      `/audits/export/${format}`,
      { properties: headerProps },
      { sortDirection, sortBy, filter, ...filterToObject(filters), offset: 0 },
    );

    var newBlob = new Blob([blob], { type: `application/${format}` });

    const fileURL = URL.createObjectURL(newBlob);
    let tempLink = document.createElement('a');
    tempLink.href = fileURL;
    tempLink.setAttribute('download', `audits.${format}`);
    tempLink.click();
  } catch (e) {
    Logger.error('failed to export audits - ', e);
  }
}

export function* rootSaga() {
  yield takeLatest(initData, initDataFunction);
  yield takeEvery(fetchMoreItems, fetchMoreItemsFunction);
  yield takeLatest(removeFilter, removeFilterFunction);
  yield takeLatest(filterData, filterDataFunction);
  yield takeLatest(
    [loadAudits, textSearch, onPageChange, setFilterData, setDataSorting, startReloading],
    loadAuditsFunction,
  );
  yield takeEvery(exportCSV, () => exportAuditsFunction('csv'));
  yield takeEvery(exportPDF, () => exportAuditsFunction('pdf'));
}
