const applicationServerPublicKey = 'BNKrdhGC2OubV7GMtHWX9uHMEk4q1DFUBx7pbG47w8A1S8J-d59szzFW0Bo7iAwPkdxlVck33MDL55NnAOJ4CYw'

function urlB64ToUint8Array(base64String: string): Uint8Array {
    const padding = '='.repeat((4 - base64String.length % 4) % 4);
    const base64 = (base64String + padding)
        .replace(/\-/g, '+')
        .replace(/_/g, '/');

    const rawData = window.atob(base64);
    const outputArray = new Uint8Array(rawData.length);

    for (let i = 0; i < rawData.length; ++i) {
        outputArray[i] = rawData.charCodeAt(i);
    }
    return outputArray;
}



export const registerServiceWorker = async (url: string): Promise<registerServiceWorker> => {
    if (!navigator.serviceWorker || !window.PushManager) {
        throw new Error('Push messaging is not supported')
    }

    const swReg = await navigator.serviceWorker.register(url, {scope: '/frontegg'})
    const subscription = await swReg.pushManager.getSubscription()
    const isSubscribed = !(subscription === null);
    // @ts-ignore
    return {swReg, subscription, isSubscribed}
}

export const subscribePushNotifications =    (swReg: ServiceWorkerRegistration): Promise<PushSubscription> => {
        const applicationServerKey = urlB64ToUint8Array(applicationServerPublicKey)
        return swReg.pushManager.subscribe({
            userVisibleOnly: true,
            applicationServerKey
        }).catch(e => {
            console.error('could not subscribe push notifications -', e)
            throw e
        })
    }
