import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators, Dispatch } from 'redux';
import { Iprops as IndexIprops } from './index';

import { registerServiceWorker, subscribePushNotifications } from './utils/registerServiceWorker';
import { Istate, setContext, handleShowMsg, handleHideMsg, subscribeOnServer } from './reducer';
import { ContextOptions } from '../../providers/context';
import { wait } from '../../helpers/wait';

const LOCAL_STORAGE_KEY_REMIND_ON = '_frontegg_webpush_remindOn';

export interface Iprops extends IndexIprops {
  context: ContextOptions;
}

class WebpushContainer extends React.Component<Iprops & StateToProps & DispatchProps> {
  swReg: any;
  state = {
    selectRemindMeLaterTime: false,
    animationClass: undefined,
  };

  constructor(props: Iprops & StateToProps & DispatchProps) {
    super(props);
    const { setContext, context } = this.props;
    setContext(context);
    this.registerSW();
  }

  registerSW = async (): Promise<void> => {
    const { serviceWorkerUrl, handleShowMsg } = this.props;
    const { swReg, isSubscribed } = await registerServiceWorker(serviceWorkerUrl);
    this.swReg = swReg;

    if (!isSubscribed && this.shouldRemind()) {
      handleShowMsg();
      this.callIfFunction(this.props.onMessageDisplayStatusChange, true);
    }
  };

  shouldRemind = (): boolean => {
    const remindOnDate = localStorage.getItem(LOCAL_STORAGE_KEY_REMIND_ON);
    if (!remindOnDate) return true;
    if (new Date() >= new Date(remindOnDate)) {
      return true;
    }
    return false;
  };

  callIfFunction = (func?: Function, ...args: any) => {
    if (typeof func === 'function') {
      func(...args);
    }
  };

  subscribe = async () => {
    const { subscribeOnServer, handleHideMsg, onSubscribe, onMessageDisplayStatusChange } = this.props;
    const pushSubscription = await subscribePushNotifications(this.swReg);
    // @ts-ignore
    await subscribeOnServer(pushSubscription);
    this.callIfFunction(onMessageDisplayStatusChange, false);
    handleHideMsg();
    this.callIfFunction(onSubscribe);
  };

  handleRemindMeLater = (days: number) => {
    const { onCancel, handleHideMsg, onMessageDisplayStatusChange } = this.props;
    const oneDayFromNow = new Date(Date.now() + 1000 * 60 * 60 * 24 * days);
    localStorage.setItem(LOCAL_STORAGE_KEY_REMIND_ON, oneDayFromNow.toString());
    handleHideMsg();
    this.callIfFunction(onMessageDisplayStatusChange, false);
    this.callIfFunction(onCancel);
  };

  onRemindLaterClick = async () => {
    this.setState({ animationClass: 'fadeOut' });
    await wait(500);
    this.setState({ selectRemindMeLaterTime: true, animationClass: 'fadeIn' });
  };

  messageContentRenderer = () => {
    if (this.state.selectRemindMeLaterTime) {
      return (
        <React.Fragment>
          <div className={'selectable'} onClick={() => this.handleRemindMeLater(1)}>
            Tomorrow
          </div>
          <div className={'selectable'} onClick={() => this.handleRemindMeLater(7)}>
            Next Week
          </div>
          <div className={'selectable'} onClick={() => this.handleRemindMeLater(30)}>
            Next Month
          </div>
          <div className={'selectable'} onClick={() => this.handleRemindMeLater(365 * 10)}>
            Never
          </div>
        </React.Fragment>
      );
    } else {
      return (
        <React.Fragment>
          <div>Do you want to enable browser notifications?</div>
          <div className={'selectable'} onClick={this.subscribe}>
            Enable
          </div>
          <div className={'selectable'} onClick={this.onRemindLaterClick}>
            Remind me later
          </div>
        </React.Fragment>
      );
    }
  };

  render() {
    if (this.props.children) {
      return <div onClick={this.subscribe}>{this.props.children}</div>;
    }
    return this.props.showWebPushMsg ? (
      <div className={'frontegg'}>
        <div className='webpush-container'>
          <div className={`flex-row ${this.state.animationClass}`}>{this.messageContentRenderer()}</div>
        </div>
      </div>
    ) : null;
  }
}

const mapStateToProps = (state: Istate) => ({
  showWebPushMsg: state.showMsg,
});

type StateToProps = ReturnType<typeof mapStateToProps>;

const mapDispatchToProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      setContext,
      handleHideMsg,
      handleShowMsg,
      subscribeOnServer,
    },
    dispatch,
  );

type DispatchProps = ReturnType<typeof mapDispatchToProps>;

export default connect(mapStateToProps, mapDispatchToProps)(WebpushContainer);
