import { FormikValues } from 'formik';
import { headerProps } from './mocks/data';
import { IWebhook, IWebHookLog } from './types/webhooks';
import { ContextOptions } from '../../providers/context';
import { IEventsProps } from './types/eventProps';
import { IHeaderProps } from './types/headerProps';
import { createSlice, createAction } from '@reduxjs/toolkit';
import { bindActionCreators, Dispatch } from 'redux';
import { bool } from 'yup';

export interface ITestWebhookData {
  url: string;
  secret: string
}

export interface WebHooksState {
  events: IEventsProps[];
  headerProps: IHeaderProps[];
  webhooks: IWebhook[];
  webhookLogs: IWebHookLog[];
  context: ContextOptions;
  isLoading: boolean;
  webhookLogsCount: number;
  successAddOrUpdate: boolean;
  limitTableLogsSize: number;
  isTesting: boolean;
  testResult: null | Record<string, string>;
  error?: { [key: string]: string };
}

export const initialState = {
  events: [],
  headerProps: headerProps,
  webhooks: [],
  webhookLogs: [],
  context: null,
  isLoading: true,
  isTesting: false,
  successAddOrUpdate: false,
  limitTableLogsSize: 10,
  webhookLogsCount: 0,
  testResult: null,
  error: {},
};

const { name, actions, reducer } = createSlice({
  name: 'webhooks',
  initialState,
  reducers: {
    setContext: (state, { payload }) => {
      state.context = payload;
    },
    startLoading: (state) => {
      state.isLoading = true;
    },
    finishLoading: (state) => {
      state.isLoading = false;
    },
    loadWebhookLogsSuccess: {
      prepare: (row: IWebHookLog[], count: number) => ({ payload: { row, count } }),
      reducer: (state, { payload: { row, count } }) => {
        state.webhookLogs = row;
        state.webhookLogsCount = count;
      },
    },
    addOrUpdateWebhookSuccessAction: (state, { payload }) => {
      state.successAddOrUpdate = payload;
    },
    loadWebhookLogsAction: {
      prepare: (payload: Record<string, string>) => ({ payload }),
      reducer: (state) => {
        state.successAddOrUpdate = false;
      },
    },
    loadWebhooksSuccess: (state, { payload }) => {
      state.webhooks = payload;
    },
    loadWebhookIsActive: (state, { payload }) => {
      state.webhooks.forEach((webhook: IWebhook) => {
        if (webhook._id === payload._id) {
          webhook.isActive = payload.isActive;
        }
      });
    },
    loadEventsSuccess: (state, { payload }) => {
      state.events = payload;
    },
    loadFailedAction: (state, { payload }) => {
      state.error = { ...state.error, [payload.name]: payload.error };
    },
    dismissErrorAction: (state) => {
      state.error = {};
    },
    testWebhookResultAction: (state, { payload }) => {
      state.testResult = payload;
      state.isTesting = false;
    },
    testWebhookAction: {
      prepare: (payload: ITestWebhookData) => ({ payload }),
      reducer: (state) => {
        state.isTesting = true;
      },
    },
  },
});

export const initData = createAction(`${name}/initData`);
export const deleteWebhookAction = createAction<string>(`${name}/deleteWebhookAction`);
export const addWebhookAction = createAction<FormikValues>(`${name}/addWebhookAction`);
export const updateWebhookAction = createAction<FormikValues>(`${name}/updateWebhookAction`);
export const updateWebhookIsActiveAction = createAction<IWebhook>(`${name}/updateWebhookIsActiveAction`);

export { reducer };

export const {
  setContext,
  startLoading,
  finishLoading,
  loadFailedAction,
  loadEventsSuccess,
  testWebhookAction,
  dismissErrorAction,
  loadWebhooksSuccess,
  loadWebhookLogsAction,
  loadWebhookLogsSuccess,
  testWebhookResultAction,
  addOrUpdateWebhookSuccessAction,
  loadWebhookIsActive,
} = actions;


export const allSagaState = ({ context, ...state }: WebHooksState) => state;
export const sagaState = (state: WebHooksState) => ({ state: allSagaState(state) });
export const allSagaActions = (dispatch: Dispatch) => bindActionCreators({
  ...actions,
  initData,
  deleteWebhookAction,
  addWebhookAction,
  updateWebhookAction,
  updateWebhookIsActiveAction,
}, dispatch);
export const sagaActions = (dispatch: Dispatch) => ({
  actions: allSagaActions(dispatch),
});
import { select, delay } from 'redux-saga/effects';
import { ContextOptions } from '../providers/context';

export function* getContextFromRedux() {
  let result;
  do {
    const { context } = yield select();
    if (!context) {
      yield delay(50);
    } else {
      result = context;
    }
  } while (!result);
  return result as ContextOptions;
}
import { FormikValues } from 'formik';
import { takeEvery, put, delay, select, all, takeLatest } from 'redux-saga/effects';
import {
  initData,
  addWebhookAction,
  startLoading,
  deleteWebhookAction,
  finishLoading,
  updateWebhookAction,
  loadWebhooksSuccess,
  loadFailedAction,
  loadEventsSuccess,
  dismissErrorAction,
  addOrUpdateWebhookSuccessAction,
  loadWebhookLogsSuccess,
  loadWebhookLogsAction,
  loadWebhookIsActive,
  updateWebhookIsActiveAction,
  testWebhookAction,
  testWebhookResultAction,
} from './reducer';
import { Get, Post, Patch, Delete } from '../../helpers/fetch';
import Logger from '../../helpers/logger';
import { getContextFromRedux } from '../../helpers/getContext';

function* initDataFunction() {
  yield put(startLoading());
  yield all([yield loadEventsFunction(), yield loadWebhooksDataFunction()]);
  yield put(finishLoading());
}

function* updateWebhooksDataFunction() {
  yield put(startLoading());
  yield loadWebhooksDataFunction();
  yield put(finishLoading());
}

function* loadEventsFunction() {
  const { context } = yield select();
  try {
    const result = yield Get(context, '/event/resources/configurations/v1/categories');
    yield put(loadEventsSuccess(result));
  } catch (e) {
    const errorMessage = {
      name: 'eventsData',
      error: e.message,
    };
    Logger.error('failed to load events data - ', e);
    yield put(loadFailedAction(errorMessage));
  }
}

function* loadWebhooksDataFunction() {
  const { context } = yield select();
  try {
    const result = yield Get(context, '/webhook');
    yield put(loadWebhooksSuccess(result));
    Logger.debug(result);
  } catch (e) {
    const errorMessage = {
      name: 'webhooksData',
      error: e.message,
    };
    Logger.error('failed to load webhooks - ', e);
    yield put(loadFailedAction(errorMessage));
  }
}

function* addOrUpdateWebhookFunction({ payload }: FormikValues) {
  const { context } = yield select();
  try {
    if (payload._id) {
      yield Patch(context, `/webhook/${payload._id}`, payload);
    } else {
      yield Post(context, `/webhook/custom`, payload);
    }
    yield updateWebhooksDataFunction();
    yield put(addOrUpdateWebhookSuccessAction(true));
  } catch (e) {
    const errorMessage = {
      name: 'addOrUpdateWebhook',
      error: 'Server error. Please try again later.',
    };
    Logger.error('failed add or update webhook - ', e);
    yield put(loadFailedAction(errorMessage));
    yield delay(5000);
    yield put(dismissErrorAction());
  }
}

function* updateWebhookIsActiveFunction({ payload }: { payload: { _id: string; isActive: boolean } }) {
  try {
    const { context } = yield select();
    const webhook = yield Patch(context, `/webhook/${payload._id}`, payload);
    yield put(loadWebhookIsActive(webhook));
  } catch (e) {
    const errorMessage = {
      name: 'updateWebhookIsActive',
      error: 'Server error. Please try again later.',
    };
    Logger.error('failed update webhook isActive status - ', e);
    yield put(loadFailedAction(errorMessage));
    yield delay(5000);
    yield put(dismissErrorAction());
  }
}

function* deleteWebhookFunction({ payload }: any) {
  const { context } = yield select();
  try {
    yield Delete(context, `/webhook/${payload}`);
    yield updateWebhooksDataFunction();
  } catch (e) {
    const errorMessage = {
      name: 'deleteWebhookAction',
      error: e.message,
    };
    Logger.error('failed delete webhook - ', e);
    yield put(loadFailedAction(errorMessage));
  }
}

function* loadWebhookLogsFunction({ payload }: any) {
  const context = yield getContextFromRedux();
  const offset = payload.limit * payload.page - payload.limit || 0;
  try {
    const { rows = [], count = 0 } = yield Get(context, '/webhook/logs', {
      id: payload.id,
      limit: payload.limit,
      offset: offset,
    });
    yield put(loadWebhookLogsSuccess(rows, count));
    yield put(dismissErrorAction());
  } catch (e) {
    const errorMessage = {
      name: 'logsWebhook',
      error: e.message,
    };
    yield put(loadFailedAction(errorMessage));
  }
}

type TTestWebhook = ReturnType<typeof testWebhookAction>;

function* testWebhookFunction({ payload }: TTestWebhook) {
  const context = yield getContextFromRedux();
  try {
    const body = yield Post(context, '/webhook/test', payload);
    yield put(testWebhookResultAction({ code: body.statusCode }));
  } catch (e) {
    yield put(testWebhookResultAction({ code: e.response?.statusCode || 400 }));
  }
}

export function* rootSaga() {
  yield takeEvery(initData, initDataFunction);
  yield takeLatest([addWebhookAction, updateWebhookAction], addOrUpdateWebhookFunction);
  yield takeLatest(loadWebhookLogsAction, loadWebhookLogsFunction);
  yield takeLatest(deleteWebhookAction, deleteWebhookFunction);
  yield takeLatest(updateWebhookIsActiveAction, updateWebhookIsActiveFunction);
  yield takeLatest(testWebhookAction, testWebhookFunction);
}
