export const EventsColumn = (props: EventsColumnProps) => {
  const { events } = props;
  const categoryLength = getTagsCount(events);

  const filledCategories = Object.keys(events).reduce((acc: { [key: string]: TEvent[] }, curr: string) => {
    if (events[curr].length > 0) {
      acc[curr] = events[curr];
    }
    return acc;
  }, {});
  const categoryCount = Object.keys(filledCategories).length;

  return (
    <>
      <div className='webhooks-table__title'>
        {Object.keys(filledCategories).map((key, idx) => {
          const categoryEventsLength = events[key][0].key.includes('*') ? '.*' : ` (${events[key].length})`;
          return (
            <React.Fragment key={key}>
              {`${key}${categoryEventsLength}${idx !== categoryCount - 1 ? ', ' : ''} `}
            </React.Fragment>
          );
        })}
      </div>
      <EventsList events={filledCategories} tagsLength={categoryLength} />
    </>
  );
};
