import React from 'react';
import moment from 'moment';
import classnames from 'classnames';
import { Table, HeaderProps, Dropdown, Modal, Button, Icon, Checkbox } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import { IWebhook } from './types/webhooks';
import { EventsColumn } from './components/table-events-column';
import SortIcon from '../../../images/sort.svg';
import { getTagsCount } from './helpers/getTagsCount';

interface IWebHooksTableProps {
  mountNode?: any;
  webhooks: IWebhook[];
  headerProps: HeaderProps[];
  deleteWebhookAction: (v: string) => void;
  updateWebhookIsActiveAction: Function;
}

enum EField {
  displayName = 'displayName',
  eventKeys = 'eventKeys',
  invocations = 'invocations',
  createdAt = 'createdAt',
  isActive = 'isActive'
}

interface IWebHooksTableState {
  openModal: string | null;
  sort: {
    field: EField;
    reverse: boolean;
  };
}

export class WebHooksList extends React.Component<IWebHooksTableProps, IWebHooksTableState> {
  state = {
    openModal: null,
    sort: { field: EField.createdAt, reverse: false }
  };

  closeModal = () => {
    this.setState({ openModal: null });
  };

  getRow = (col: HeaderProps, webhook: any) => {
    switch (col.type.toLowerCase()) {
      case 'title':
        return (
          <Link to={`/webhook/${webhook._id}`}>
            <div className='webhooks-table__title'>{webhook[col.name]}</div>
            <div className='webhooks-table__description'>{webhook.description}</div>
          </Link>
        );

      case 'timestamp':
        return (
          <>
            <div className='webhooks-table__title'>{moment.utc(webhook[col.name]).local().fromNow()}</div>
            <div className='webhooks-table__description'>
              {moment.utc(webhook[col.name]).local().format('D/M/YYYY hh:mm')}
            </div>
          </>
        );

      case 'events':
        return <EventsColumn events={webhook[col.name]}/>;

      case 'invocations':
        return <div className='webhooks-table__title webhooks-table__center'>{webhook[col.name] || '0'}</div>;

      case 'boolean':
        return (
          <div className='mr-n5'>
          <Checkbox
            toggle
            checked={!!webhook.isActive}
            onChange={() => this.props.updateWebhookIsActiveAction({ _id: webhook._id, isActive: !webhook.isActive })}
            className='webhooks-table__toggle'
          />
          </div>
        );

      default:
        return (
          <div className='webhooks-table__title'>
            {typeof webhook[col.name] === 'object' ? JSON.stringify(webhook[col.name]) : webhook[col.name]}
          </div>
        );
    }
  };

  handleChangeSort = (field: EField) => {
    const { sort } = this.state;
    if (sort.field === field) this.setState({ sort: { ...sort, reverse: !sort.reverse } });
    else this.setState({ sort: { field, reverse: false } });
  }

  sortWebhooks = (webhooks: IWebhook[]) => {
    const { sort } = this.state;
    const sorted = webhooks.sort((a: IWebhook, b: IWebhook) => {
      const { field } = sort;
      const value1 = a[field];
      const value2 = b[field];

      switch(field) {
        case EField.displayName: return value1.toLowerCase() > value2.toLowerCase() ? 1 : -1;
        case EField.eventKeys: return getTagsCount(value1) - getTagsCount(value2);
        case EField.createdAt: return +(new Date(value2)) - +(new Date(value1));
        case EField.invocations:
        case EField.isActive: return +value1 - +value2;
        default: return 0;
      }
    })
    return sort.reverse ? sorted.reverse() : sorted;
  }

  isFieldReverse = (field: EField) => this.state.sort.field === field && !this.state.sort.reverse;

  render() {
    const { webhooks, headerProps, deleteWebhookAction, mountNode } = this.props;

    return (
      <Table unstackable basic className='webhooks-table__table'>
        <Table.Header>
          <Table.Row>
            {headerProps.map((col) => {
              const isCenterAlign = EField.invocations === col.name || EField.isActive === col.name;
              return (
              <Table.HeaderCell className={classnames(Object.values(EField).includes(col.name) && 'clickable', isCenterAlign && 'align-center')} key={col._id} onClick={() => this.handleChangeSort(col.name)}>
                <span className='webhooks-table__header-title'>{col.displayName}</span>
                {col.displayName && (
                  <img
                    className={classnames('webhooks-table__sort-icon', this.isFieldReverse(col.name) && 'reverse')}
                    src={SortIcon}
                    alt='sort' />
                )}
              </Table.HeaderCell>
            )})}
            <Table.HeaderCell></Table.HeaderCell>
          </Table.Row>
        </Table.Header>

        <Table.Body>
          {webhooks &&
          this.sortWebhooks(webhooks).map((webhook: IWebhook, idx: number) => (
            <Table.Row key={idx}>
              {headerProps.map((col) => {
                  const isCenterAlign = [EField.invocations, EField.isActive].includes(col.name)
                  return <Table.Cell className={classnames(isCenterAlign && 'align-center')} key={col._id}>{this.getRow(col, webhook)}</Table.Cell>;
              })}
              <Table.Cell width={1}>
                <Dropdown icon={<div className='webhooks__options'><span/></div>}>
                  <Dropdown.Menu direction='left' className='webhooks-table__submenu'>
                    <Dropdown.Item as={Link} to={`/webhook/${webhook._id}`}>
                      <div>
                        <Icon name='edit outline'/> Edit
                      </div>
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => this.setState({ openModal: webhook._id })}>
                      <div>
                        <Icon name='close'/> Remove
                      </div>
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
                <Modal
                  size='mini'
                  closeOnDimmerClick
                  mountNode={mountNode}
                  onClose={this.closeModal}
                  open={this.state.openModal === webhook._id}>
                  <Modal.Content>
                    Are you sure you want to delete? <br/> This ACTION CANNOT BE UNDONE
                  </Modal.Content>
                  <Modal.Actions>
                    <Button onClick={this.closeModal} color='red' inverted>
                      <Icon name='remove'/> No
                    </Button>
                    <Button
                      onClick={() => {
                        deleteWebhookAction(webhook._id);
                        this.closeModal();
                      }}
                      color='green'
                      inverted>
