import { TEvent } from './../types/eventProps';
import { IWebhook } from './../types/webhooks';
import { createSelector } from 'reselect';
import { WebHooksState } from '../reducer';

interface TPreparedEvents {
  [key: string]: {}[];
}

const events = (state: WebHooksState) => state.events;
const webhooks = (state: WebHooksState) => state.webhooks;

export const preparedEvents = createSelector([events], (events: any[]) =>
  events.reduce((acc, curr) => {
    acc[curr.name] = curr.events.map((event: TEvent) => ({ displayName: event.displayName, key: event.key }));
    return acc;
  }, {}),
);

export const preparedWebhooks = createSelector(
  [preparedEvents, events, webhooks],
  (preparedEvents: TPreparedEvents, events: any[], webhooks) => {
    const result = webhooks.map((webhook: IWebhook) => ({
      ...webhook,
      eventKeys: events.reduce((acc, curr) => {
        const category = curr.name;
        const sortedByCategoryEvents = preparedEvents[category].filter((event: { [key: string]: string }) =>
          webhook.eventKeys.find((eventKey: string) => event.key === eventKey),
        );

        if (webhook.eventKeys.includes(`${category}.*`)) {
          acc[category] = [{ displayName: 'All', key: `${category}.*` }];
        } else {
          acc[category] = sortedByCategoryEvents;
        }

        return acc;
      }, {}),
    }));

    return result;
  },
);
