import React, { Component } from 'react';
import { Dropdown } from 'semantic-ui-react';
import { Field, FieldProps } from 'formik';
import { IEventsProps, TEvent } from '../types/eventProps';

interface SelectedEventsProps {
  selectedEvents: Array<string>;
  events: Array<IEventsProps>;
  className?: string;
}

export default class SelectedEvents extends Component<SelectedEventsProps, {}> {
  state = {
    searchQuery: '',
  };

  handleChange = (setFieldValue: (key: string, []) => void, values: any) => {
    const { events } = this.props;
    const newData = events.reduce((acc: { [key: string]: any }, category: IEventsProps) => {
      if (values.includes(`${category.name}.*`)) {
        acc[category.name] = [`${category.name}.*`];
      } else {
        const sortedEvents = category.events
          .filter((event: TEvent) => values.includes(event.key) || values.includes(`${category.name}.*`))
          .map((e) => (e.key ? e.key : e));
        acc[category.name] = sortedEvents;
      }
      return acc;
    }, {});
    this.setState({ searchQuery: '' });
    Object.keys(newData).forEach((key) => setFieldValue(key, newData[key]));
  };

  handleSearchChange = (_e: any, { searchQuery }: any) => this.setState({ searchQuery });

  render() {
    const { searchQuery } = this.state;
    const { selectedEvents } = this.props;
    const options = this.props.events.reduce((acc: {}[], curr: IEventsProps) => {
      const arr = curr.events.map(({ displayName, key }: any, index: number) => ({
        key: `${curr.name}_${index}`,
        text: displayName,
        value: key,
      }));
      const category = {
        key: `${curr.name}`,
        text: `${curr.name}.*`,
        value: `${curr.name}.*`,
      };

      if (selectedEvents.includes(category.value)) {
        return [...acc, category];
      }

      return [...acc, ...arr, category];
    }, []);

    return (
      <Field>
        {({ form }: FieldProps) => (
          <div className={this.props.className}>
            <Dropdown
              search
              selection
              icon='search'
              multiple={true}
              options={options}
              placeholder='Add new event...'
              searchQuery={searchQuery}
              value={this.props.selectedEvents}
              onSearchChange={this.handleSearchChange}
              onChange={(_e, { value }) => {
                this.handleChange(form.setFieldValue, value);
              }}
            />
            <div className='error-message'>{form.errors.events || ''}</div>
          </div>
        )}
      </Field>
    );
  }
}
import React from 'react';
