interface SelectAllProps {
  name: string;
  values: object[];
  form: FormikValues;
  className?: string;
}

interface SelectAllState {
  isSelectedCategory: boolean;
  preparedValues: string[] | [];
}

export default class SelectAll extends React.Component<SelectAllProps, SelectAllState> {
  state = {
    isSelectedCategory: false,
    preparedValues:
      this.props.values.reduce((acc: string[], curr: { [key: string]: any }) => {
        acc.push(curr.key);
        return acc;
      }, []) || [],
  };

  componentDidMount() {
    this.checkSelectedAllOrNot();
  }

  componentDidUpdate() {
    this.checkSelectedAllOrNot();
  }

  checkSelectedAllOrNot = () => {
    const { form, name } = this.props;
    const { isSelectedCategory } = this.state;

    if (isSelectedCategory && form.values[name][0] !== `${name}.*`) {
      this.setState({ isSelectedCategory: false });
    }
  };

  onSelect = (name: string, preparedValues: string[]) => {
    const { form } = this.props;
    const { isSelectedCategory } = this.state;
    if (!isSelectedCategory) {
      form.setFieldValue(name, preparedValues);
    } else {
      form.setFieldValue(name, []);
    }
    this.setState({ isSelectedCategory: !this.state.isSelectedCategory });
  };

  render() {
    const { name, className } = this.props;

    return (
      <div className='webhooks-form__dropdown-item-selectAll webhooks-form__dropdown-items'>
        <Checkbox
          checked={this.state.isSelectedCategory}
          label='Select all on this Category'
          onChange={() => this.onSelect(name, [`${name}.*`])}
        />
      </div>
    );
  }
}
import React from 'react';
import { Field, FieldProps } from 'formik';
import { TEvent } from '../types/eventProps';
import { Checkbox } from 'semantic-ui-react';

interface ICheckboxProps {
  name: string;
  index: number;
  category: string;
  eventKey: string;
  events: { [key: string]: TEvent[] | any[] };
}

export const FormCheckbox = ({ eventKey, ...props }: ICheckboxProps) => {
  return (
    <Field name={props.category}>
      {({ field, form }: FieldProps) => (
        <Checkbox
          {...props}
          checked={(field.value && field.value.includes(eventKey)) || field.value.includes(`${field.name}.*`)}
          onChange={() => {
            if (field.value && field.value.includes(eventKey)) {
              const nextValue = field.value.filter((value: string): boolean => value !== eventKey);
              form.setFieldValue(props.category, nextValue);
            } else if (field.value.includes(`${field.name}.*`)) {
              const nextValue = props.events[props.category]
                .filter((e: TEvent) => e.key !== eventKey)
                .map((ev: TEvent) => ev.key);
              form.setFieldValue(props.category, nextValue);
            } else {
              const nextValue = field.value.concat(eventKey);
              form.setFieldValue(props.category, nextValue);
            }
          }}
          label={props.name}
        />
      )}
    </Field>
  );
};
