      if (formikValues[name].includes(`${name}.*`)) {
        acc.push(...events.map((e) => e.key));
      } else {
        acc.push(...formikValues[name]);
      }
      return acc;
    }, []).length;

    const {testResult} =  this.state

    return (
      <>
        <Form>
          <Grid className='webhooks-form'>
            <GridRow>
              <GridColumn width={8}>
                <h4>General settings</h4>
              </GridColumn>
              <GridColumn width={8}>
                <h4>Event settings</h4>
              </GridColumn>
            </GridRow>
            <GridRow>
              <GridColumn className='webhooks-form__left' width={8}>
                <Field>
                  {({ form }: FieldProps) => {

                    const handleSecretOrUrlChange = (e: unknown) => {
                      this.setState({
                        testResult: null
                      })
                      form.handleChange(e)
                    }

                    return(
                      <>
                        <FormInput
                          type='text'
                          name='displayName'
                          title='Display Name'
                          placeholder='Input name...'
                          className='webhooks-form__input'
                          value={formikValues.displayName}
                          onChange={form.handleChange}
                        />
                        <label className='webhooks-form__label'>Description</label>
                        <TextArea
                          name='description'
                          placeholder='Add short description'
                          className='webhooks-form__input-description'
                          value={formikValues.description}
                          onChange={form.handleChange}
                        />
                        <FormInput
                          type='text'
                          name='url'
                          title='URL'
                          placeholder='https://'
                          className='webhooks-form__input'
                          value={formikValues.url}
                          onChange={handleSecretOrUrlChange}
                        />
                        <Popup
                          className='hint-container'
                          position='right center'
                          content='JWT signed with this secret will be sent in the header "frontegg-secret" of the webhook request'
                          trigger={<img src={helpIcon} alt='help' className='webhooks__help-icon' />} />
                        <FormInput
                          type='text'
                          name='secret'
                          title='Secret Key'
                          placeholder='Secret key'
                          className='webhooks-form__input'
                          value={formikValues.secret}
                          onChange={handleSecretOrUrlChange}
                        />
                        <div className='webhooks-form__button-container'>
                          <Button
                            type='submit'
                            className={classnames(
                              'webhooks-form__button webhooks__button',
                              form.dirty || (success && 'webhooks__button-success'),
                            )}
                            disabled={form.dirty && !isEmpty(form.errors)}>
                            {this.props.buttonText}
                            {success && <Icon name='check' />}
                          </Button>
                          <Button
                            type='button'
                            loading={this.props.isTesting}
                            className={`webhooks-form__button webhooks__button webhooks__button-${testResult?.toLowerCase()}`}
                            disabled={!form.values.url || !form.values.secret}
                            onClick={() => this.props.testFunction(form.values.url, form.values.secret)}>
                            {testResult || 'Test Hook'}
                          </Button>
                          {error?.addOrUpdateWebhook && (
                            <span className='webhooks-form__error'>{error.addOrUpdateWebhook}</span>
                          )}
                        </div>
                      </>
                    )}}
                </Field>
              </GridColumn>
              <GridColumn className='webhooks-form__right' width={8}>
                <div className='webhooks-form__right-info'>
                  <span className='webhooks-form__right-selected'>
                    {allSelectedEventsLength !== 0 ? <>Selected: {allSelectedEventsLength}</> : 'None selected'}
                  </span>
                  <h5>Selected Events</h5>
                  <SelectedEvents
                    className='webhooks-form__selected-events'
                    events={this.props.events}
                    selectedEvents={allSelectedEvents}
                  />
                  <h5>Manage categories</h5>
                  <DropdownSelector formikValues={formikValues} dropdownItems={this.props.events} />
                </div>
              </GridColumn>
            </GridRow>
          </Grid>
        </Form>
        {testResult && this.props.testResult && (
          <Modal
            open={true}
            mountNode={this.state.mountNode}
            onClose={this.props.onCloseResult}
            size='tiny'
            className={`webhooks-form__dialog webhooks-form__dialog-${testResult.toLowerCase()}`}>
            <Modal.Content>
              <div>
                {testResult}: Delivery URL returned response code: {this.props.testResult.code} <br />
                <span className='webhooks-form__dialog-note'>{moment().format('D/M/YYYY hh:mm')}</span>
              </div>
              <div>
                <span
                  className={`webhooks-logs__title-status webhooks-logs__title-status-${testResult.toLowerCase()}`}>
                  {testResult}
                </span>
              </div>
            </Modal.Content>
          </Modal>
        )}
      </>
    );
  }
}
export const urlValidation = new RegExp(
  '^(https?:\\/\\/)?' + // protocol
  '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|' + // domain name
  '((\\d{1,3}\\.){3}\\d{1,3}))' + // OR ip (v4) address
  '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*' + // port and path
  '(\\?[;&a-z\\d%_.~+=-]*)?' + // query string
    '(\\#[-a-z\\d_]*)?$',
  'i',
);

export const validate = (values: any, eventsFields: any) => {
  const errors: any = {};
  if (!values.url) {
    errors.url = 'Url is required field';
  } else if (!urlValidation.test(values.url)) {
    errors.url = 'Invalid url address';
  }
  if (!values.displayName) {
    errors.displayName = 'Display name is required field';
  }
  if (values.secret && values.secret.length < 8) {
    errors.secret = 'Minimum 8 characters';
  }

  const eventsFieldsLength = Object.keys(eventsFields).reduce((acc: string[], curr) => {
    acc.push(...values[curr]);
    return acc;
  }, []).length;

  if (!eventsFieldsLength) {
    errors['events'] = 'You should choose minimum one event';
  }

  return errors;
};
import React from 'react';
import Logs from './webhooks-logs';
import { connect } from 'react-redux';
import { IWebhook } from './types/webhooks';
import { TEvent } from './types/eventProps';
import { Formik, FormikValues } from 'formik';
import { WebHooksForm } from './webhooks-form';
import { Loader, TabProps } from 'semantic-ui-react';
import { validate } from './helpers/validateForm';
import { bindActionCreators, Dispatch } from 'redux';
import { preparedWebhooks } from './helpers/selectors';
import { RouteComponentProps, Prompt } from 'react-router';
import {
  WebHooksState,
  addWebhookAction,
  testWebhookAction,
  updateWebhookAction,
  loadWebhookLogsAction,
  testWebhookResultAction,
} from './reducer';
import { PageHeader } from '../PageHeader';

interface MatchParams {
  id?: string;
}

interface WebHooksFormContainerProps extends RouteComponentProps<MatchParams> {
}

interface WebHooksFormContainerState {
  editMode: boolean;
  activeIndex?: number | string;
}

class WebHooksFormContainer extends React.Component<WebHooksFormContainerProps & TMapDispatchToProps & TMapStateToProps,
  WebHooksFormContainerState> {
  state = {
    editMode: !!this.props.match.params.id,
    activeIndex: 0,
  };

  componentDidMount() {
    !!this.props.match.params.id &&
    this.props.loadWebhookLogsAction({
      id: this.props.match.params.id,
      page: '1',
      limit: `${this.props.limitTableLogsSize}`,
    });
  }

  prepareInitialValues = (webhook: IWebhook | undefined, eventsFields: {}) => {
    const initialValues = {
      displayName: webhook?.displayName || '',
      description: webhook?.description || '',
      url: webhook?.url || '',
      secret: webhook?.secret || '',
      ...eventsFields,
    };

    return initialValues;
  };

  prepareWebhook = (values: FormikValues) => {
    const { webhooks, events } = this.props;
    const { displayName, description, url, secret, ...selectedEvents } = values;
    const webhook = webhooks.find((webhook) => webhook._id === this.props.match.params.id);
    const preparedEventKeys = Object.keys(selectedEvents).reduce((acc: string[], curr: string) => {
      acc.push(...selectedEvents[curr]);
      return acc;
    }, []);

    return {
      displayName,
      description,
      url,
      secret,
      eventKeys: preparedEventKeys,
      ...(webhook && { _id: webhook._id }),
    };
  };

  render() {
    const { activeIndex, editMode } = this.state;
    const {
      error,
      events,
      webhooks,
      isLoading,
      isTesting,
      testResult,
      addWebhookAction,
      successAddOrUpdate,
      updateWebhookAction,
      testWebhookResultAction,
    } = this.props;
    const webhook = webhooks.find((webhook) => webhook._id === this.props.match.params.id);

    const eventsFields = events.reduce((acc: { [key: string]: string[] }, { name: category }) => {
      acc[category] = webhook?.eventKeys[category].map((event: TEvent) => event.key) || [];
      return acc;
    }, {});

    const initialValues: FormikValues = this.prepareInitialValues(webhook, eventsFields);


    const tabs: TabProps | undefined = editMode ? {
      panes: [{ menuItem: 'Details' }, { menuItem: 'Logs' }],
      activeIndex,
      onTabChange: (_, { activeIndex }) => this.setState({ activeIndex }),
    } : undefined;


    return (
      <>
        <PageHeader title={editMode ? webhook?.displayName : 'Add New Hook'}
                    tabs={tabs}
                    onBackButtonClick={this.props.history.goBack}/>
        <div className='fe-page-content'>
          {isLoading ? <Loader className='webhooks-loader' active inline='centered'/> :
            <>
              {activeIndex === 0 && <Formik
                initialValues={initialValues}
                validate={(values) => validate(values, eventsFields)}
                onSubmit={(values) => {
                  !editMode
                    ? addWebhookAction(this.prepareWebhook(values))
                    : updateWebhookAction(this.prepareWebhook(values));
                }}>
                {({ values, isValid, dirty }) => (
                  <>
                    <Prompt
                      when={dirty && !isValid}
                      message='Are you sure you want to leave? You have unsaved changes.'
                    />
                    <WebHooksForm
                      error={error}
                      events={events}
                      formikValues={values}
                      isTesting={isTesting}
                      onCloseResult={() => testWebhookResultAction(null)}
                      testResult={testResult}
                      success={editMode && successAddOrUpdate}
                      testFunction={(url, secret) => this.props.testWebhookAction({ url, secret })}
                      buttonText={editMode ? (successAddOrUpdate ? 'Updated' : 'Update Hook') : 'Add Hook'}
                    />
                  </>
                )}
              </Formik>}
              {activeIndex === 1 && <Logs
                webhookLogs={this.props.webhookLogs}
                error={this.props.error?.logsWebhook}
                webhookId={this.props.match.params.id}
                limitTableSize={this.props.limitTableLogsSize}
                loadWebhookLogsAction={this.props.loadWebhookLogsAction}
                webhookLogsCount={this.props.webhookLogsCount}
              />}
            </>
          }
        </div>
      </>
    );
  }
}

const mapStateToProps = (state: WebHooksState) => ({
  error: state.error,
  events: state.events,
  webhooks: preparedWebhooks(state),
  isTesting: state.isTesting,
  isLoading: state.isLoading,
  testResult: state.testResult,
  webhookLogs: state.webhookLogs,
  webhookLogsCount: state.webhookLogsCount,
  successAddOrUpdate: state.successAddOrUpdate,
  limitTableLogsSize: state.limitTableLogsSize,
});

const mapDispatchProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      addWebhookAction,
      testWebhookAction,
      testWebhookResultAction,
      updateWebhookAction,
      loadWebhookLogsAction,
    },
    dispatch,
  );

type TMapDispatchToProps = ReturnType<typeof mapDispatchProps>;

type TMapStateToProps = ReturnType<typeof mapStateToProps>;

export default connect<TMapStateToProps, TMapDispatchToProps, WebHooksFormContainerProps, WebHooksState>(
  mapStateToProps,
  mapDispatchProps,
)(WebHooksFormContainer);
