      state.isLoading = true;
    },
    finishLoading: (state) => {
      state.isLoading = false;
    },
    setContext: (state, { payload }) => {
      state.context = payload;
    },
    loadProfileDataSuccess: (state, { payload }) => {
      state.error = {};
      state.basicData = payload;
    },
    loadFiledAction: (state, { payload }) => {
      state.error = { ...state.error, [payload.name]: payload.error };
    },
  },
});

export const removePhotoAction = createAction(`${name}/removePhoto`);
export const loadProfileData = createAction(`${name}/loadProfileData`);
export const uploadPhotoAction = createAction<IUploadPhoto>(`${name}/uploadPhoto`);
