export const updateBasicDataAction = createAction<IUpdateBasicData>(`${name}/updateBasicData`);

export { reducer };

export const { setContext, loadProfileDataSuccess, loadFiledAction, startLoading, finishLoading } = actions;
import { select, put, takeLatest } from 'redux-saga/effects';
import Logger from '../../helpers/logger';
import { Get, Post, Patch } from '../../helpers/fetch';
import {
  loadProfileData,
  startLoading,
  finishLoading,
  loadProfileDataSuccess,
  uploadPhotoAction,
  loadFiledAction,
  removePhotoAction,
  updateBasicDataAction,
  resetPasswordAction,
} from './reducer';
import { IUploadPhoto, IUpdateBasicData, IResetPassword } from './types';

function* loadProfileDataFunction() {
  const { context } = yield select();
  try {
    yield put(startLoading());
    const result = yield Get(context, '/team/resources/profile/me/v1');
    yield put(loadProfileDataSuccess(result));
  } catch (e) {
    const errorMessage = {
      name: 'profileData',
      error: e,
    };
    yield put(loadFiledAction(errorMessage));
    Logger.error('failed to load profile data - ', e);
  }
  yield put(finishLoading());
}

function* updateBasicDataFunction({ payload }: { payload: IUpdateBasicData }) {
  const { context } = yield select();
  try {
    yield put(startLoading());
    yield Patch(context, '/team/resources/profile/me/v1', payload);
    yield loadProfileDataFunction();
  } catch (e) {
    const errorMessage = {
      name: 'updateProfileData',
      error: e,
    };
    yield put(loadFiledAction(errorMessage));
    Logger.error('failed to update profile data - ', e);
  }
  yield put(finishLoading());
}

function* resetPasswordFunction({ payload }: { payload: IResetPassword }) {
  const { context } = yield select();

  try {
    yield put(startLoading());
    yield Post(context, '/team/resources/profile/me/resetPassword/v1', payload);
  } catch (e) {
    const errorMessage = {
      name: 'resetPassword',
      error: e,
    };
    Logger.error('failed to reset password - ', e);
    yield put(loadFiledAction(errorMessage));
  }
  yield put(finishLoading());
}

function* uploadPhotoFunction({ payload }: { payload: IUploadPhoto }) {
  const { context } = yield select();
  try {
    yield Patch(context, '/team/resources/profile/me/v1', payload);
  } catch (e) {
    const errorMessage = {
      name: 'uploadPhoto',
      error: e,
    };
    Logger.error('failed to upload photo - ', e);
    yield put(loadFiledAction(errorMessage));
  }
}

function* removePhotoFunction() {
  const { context } = yield select();
  try {
    yield Patch(context, '/team/resources/profile/me/v1', { profileImage: null });
  } catch (e) {
    const errorMessage = {
      name: 'removePhoto',
      error: e,
    };
    Logger.error('failed to remove photo - ', e);
    yield put(loadFiledAction(errorMessage));
  }
}

export function* rootSaga() {
  yield takeLatest(loadProfileData, loadProfileDataFunction);
  yield takeLatest(updateBasicDataAction, updateBasicDataFunction);
  yield takeLatest(uploadPhotoAction, uploadPhotoFunction);
  yield takeLatest(removePhotoAction, removePhotoFunction);
  yield takeLatest(resetPasswordAction, resetPasswordFunction);
}
