interface IProps {
  image?: string;
  userName: string;
  onRemovePhoto: () => void;
  onChange: (file: IUploadPhoto) => void;
}

interface IState {
  picture: string;
}

export class UploadPhoto extends React.Component<IProps, IState> {
  state = { picture: this.props.image || '' };

  componentWillUnmount() {
    URL.revokeObjectURL(this.state.picture);
  }

  async onUpload(event: any) {
    const type = event.target.files[0].type;
    if (type === 'image/jpeg' || type === 'image/png') {
      this.setState({ picture: URL.createObjectURL(event.target.files[0]) });
      const base64Img = await getBase64(event.target.files[0]);
      this.props.onChange({ profileImage: base64Img });
    }
  }

  onDelete() {
    this.setState({ picture: '' });
    this.props.onRemovePhoto();
  }

  render() {
    const { userName } = this.props;
    const { picture } = this.state;

    const capitalUserNameLetters = userName
      .match(/\b(\w)/g)
      ?.join('')
      .toUpperCase();

    return (
      <div className='uploadPhoto'>
        <div className='uploadPhoto__preview'>
          {picture ? (
            <>
              <img className='uploadPhoto__previewImage' src={picture} />
              <div onClick={() => this.onDelete()} className='uploadPhoto__delete'>
                <CrossIcon className='clearIcon' />
              </div>
            </>
          ) : (
            <div className='uploadPhoto__previewImage'>{capitalUserNameLetters}</div>
          )}
        </div>
        <div className='uploadPhoto__main'>
          <div className='uploadPhoto__userName'>{userName}</div>
          <div className='uploadPhoto__button'>
            <label className='buttonProfile uploadButton' htmlFor='file'>
              Upload Photo
            </label>
            <input type='file' id='file' onChange={(event) => this.onUpload(event)} />
            <div>At least 256 x 256px PNG or JPEG file</div>
          </div>
        </div>
      </div>
    );
  }
}
