function* loadConfigurationsFunction() {
  const { context } = yield select();

  yield put(loadingStart());

  try {
    const result = yield Get(context, samlURL);
    yield put(getConfigurationsSuccess(result));
    if (result.validated) {
      yield getVendorConfig();
    }
  } catch (e) {
    Logger.error('failed to load configuration - ', e);
    yield setInitialSamlConfig();
  } finally {
    yield put(loadingSuccess());
  }
}

function* getVendorConfig() {
  const { context } = yield select();
  try {
    const result = yield Get(context, vendorConfig);
    yield put(setVendorInformation(result));
  } catch (e) {
    Logger.error('failed to load configuration - ', e);
  }
}

