  const { context } = yield select();
  console.log(payload);
  yield put(savingStart());
  try {
    const result = yield Post(context, samlURL, payload);
    yield put(setConfiguration(result));
  } catch (e) {
    Logger.error('failed to update configuration - ', e);
  } finally {
    yield put(savingCompleted());
  }
}

function* validateDomainFunction() {
  const { context } = yield select();
  yield put(setDomainValidationError());
  yield put(savingStart());

  try {
    const result = yield Put(context, validateDomainUrl);
    yield put(setConfiguration(result));
    if (result.validated) {
      yield getVendorConfig();
      yield put(redirectTrue())
      yield delay(300)
      yield put(redirectFalse())
    }
  } catch (e) {
    Logger.error('failed to validate domain - ', e);
    yield put(
      setDomainValidationError(
        'Validation did not succeed, please notice that DNS records might take some time to update. Please try again.',
      ),
    );

  }

  yield put(savingCompleted());
}

function* setInitialSamlConfig() {
  const config: any = { enabled: false };
  yield put(getConfigurationsSuccess(config));
}

const getData = (file: File) =>
  new Promise((resolve) => {
    const reader = new FileReader();
    reader.readAsText(file);
    reader.onload = () => resolve(reader.result);
  });

function* saveConfigurationsAutoFunction({ payload }: any) {
  const { context } = yield select();
  const { configFile } = payload;
  yield put(savingStart());
  try {
    const metadata = yield getData(configFile[0]);
    const result = yield Put(
      { ...context, additionalHeadersResolver: () => [{ key: 'Content-Type', value: 'application/xml' }] },
      metadataURL,
      { metadata },
    );
    yield put(setConfiguration(result));
  } catch (e) {
    Logger.error('failed send data - ', e);
  }
  yield put(savingCompleted());
}

export function* rootSaga() {
  yield takeLatest(loadConfigurations, loadConfigurationsFunction);
  yield takeLatest(saveConfigurations, saveConfigurationsFunction);
  yield takeLatest(validateDomain, validateDomainFunction);
  yield takeLatest(saveConfigurationsAuto, saveConfigurationsAutoFunction);
}
