interface IButtonCopyProps {
  value?: string | number;
  className?: string;
}

interface IButtonCopyState {
  copied: boolean;
}
export default class extends React.Component<IButtonCopyProps, IButtonCopyState> {
  state = {
    copied: false,
  };

  onCopy = () => {
    const { value } = this.props;
    copy(`${value}`);
    this.setState({ copied: true });
    setTimeout(() => this.setState({ copied: false }), 2000);
  };

  render() {
    const { copied } = this.state;
    const { value, className } = this.props;
    return value ? (
      <Button className={classnames('icon', className)} basic type='button' onClick={this.onCopy}>
        <Icon name={copied ? 'checkmark' : 'copy'} />
      </Button>
    ) : null;
  }
}
import * as Yup from 'yup';

export const checkDomain = (value: string) => {
  try {
    Yup.string()
      .required('The domain is required')
      .matches(
        /^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([\-\.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/i,
        'The domain should be valid',
      )
      .validateSync(value);
  } catch (e) {
    return e.message;
  }
};

export const checkUrl = (value: string) => {
  if (!value) {
    return 'This field is required';
  }

  try {
    Yup.string().required().url().validateSync(value);
  } catch (e) {
    return e.message;
  }
};

export const checkString = (value: string) => {
  if (!value) {
    return 'This field is required';
  }

  try {
    Yup.string().required().validateSync(value);
  } catch (e) {
    return e.message;
  }
};
