export class SsoGuide extends React.Component<IProps> {
  state = {
    mountNode: null,
    activeIndex: 0,
    openModal: false,
  };

  componentDidMount() {
    const node = document.querySelector('#sso-configuration');
    if (node) {
      this.setState({ mountNode: node });
    }
  }

  handleClick = (_e: any, titleProps: any): any => {
    const { index } = titleProps;
    const { activeIndex } = this.state;
    const newIndex = activeIndex === index ? -1 : index;

    this.setState({ activeIndex: newIndex });
  };

  render() {
    const { guide, steps } = this.props;
    const { mountNode, activeIndex } = this.state;

    return (
      <div className='sso-guide'>
        <div className='sso-guide__title'>Guide</div>
        <div className='sso-guide__desc'>Helpful information explaining the process</div>
        <div className='sso-guide__steps'>
          {guide.map((step: string, idx: number) => (
            <div key={idx} className='sso-guide__step'>
              <ArrowRightSVG className='sso-guide__arrow' /> {step}
            </div>
          ))}
        </div>
        {steps && steps.length > 0 && <div className='sso-guide__instruction'>
          Detailed step by step{' '}
          <span className='sso-text-color' onClick={() => this.setState({ openModal: true })}>
            instruction
          </span>
          <ArrowBackSVG className='sso-svg-color' />
        </div>}
        <Modal
          open={this.state.openModal}
          closeOnEscape={true}
          closeOnDimmerClick={true}
          className='sso-instruction-modal'
          mountNode={mountNode}
          onClose={() => this.setState({ openModal: false })}>
          <span className='modal-cross-icon' onClick={() => this.setState({ openModal: false })}>
            <CrossIcon />
          </span>
          <Modal.Header>Step by step instruction</Modal.Header>
          <Modal.Content image>
            <Modal.Description className="sso-instruction-steps">
              <Accordion>
                {steps.map((step, idx) => {
                  return (
                    <>
                      <Accordion.Title active={activeIndex === idx} index={idx} onClick={this.handleClick}>
                        <span className={classnames(activeIndex === idx && 'sso-text-color')}>Step {idx + 1}</span>
                        <span className='modal-icon'>
                          <ArrowRightSVG className={classnames(activeIndex === idx && 'sso-svg-color')} />
                        </span>
                      </Accordion.Title>
                      <Accordion.Content active={activeIndex === idx}>{step}</Accordion.Content>
                    </>
                  )
                })}
              </Accordion>
            </Modal.Description>
          </Modal.Content>
        </Modal>
      </div >
    );
  }
}
