import { setConfiguration, saveConfigurations, validateDomain, ISsoConfigurationState } from './reducer';
import { Input, Button } from 'semantic-ui-react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { FieldForm } from '../common/FieldForm';
import ButtonCopy from '../common/ButtonCopy/ButtonCopy';
import { checkDomain } from '../../helpers/validators';
