interface IProps {
  history?: any;
}

class SsoDomainConfiguration extends React.Component<IProps & DispatchProps & StateToProps> {
  state = {
    recordNameCopied: false,
    recordValueCopied: false,
  };

  componentDidUpdate() {
    if(this.props.redirect) {
      this.props.history.push('/');
    }
  }

  renderDomainClaim() {
    const { samlConfiguration, saveConfigurations, isSaving, domainValidationError } = this.props;

    return (
      <div className='claim-domain'>
        <Formik
          initialValues={{ domain: samlConfiguration?.domain || '' }}
          validate={(values) => {
            const errors: any = {};
            if (!values.domain) {
              errors.domain = 'Required';
              return errors;
            }
          }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              const newConfiguration: any = {
                ...samlConfiguration,
                domain: values.domain,
              };

              saveConfigurations(newConfiguration);
              setSubmitting(false);
            }, 500);
          }}>
          {({ values, handleChange, handleBlur, dirty, isValid }) => (
            <Form>
              <FieldForm className='sso-domain'>
                <Field
                  component={Input}
                  validate={checkDomain}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.domain}
                  placeholder='Your Domain'
                  name='domain'
                  input={{ name: 'domain' }}
                />
                <ErrorMessage name='domain' component='div' className='field-error' />
              </FieldForm>
              <Button className='proceed-button' type='submit' loading={isSaving} disabled={!dirty || !isValid}>
                Proceed
              </Button>
              {domainValidationError && <div>{domainValidationError}</div>}
            </Form>
          )}
        </Formik>
      </div>
    );
  }

  renderVerificationFlow(history: any) {
    const { samlConfiguration, isSaving, validateDomain, domainValidationError, saveConfigurations } = this.props;

    const recordName = `_saml-domain-challenge.${samlConfiguration?.domain}`;
    const recordValue = samlConfiguration?.generatedVerification || '';

    const initialValues = {
      domain: samlConfiguration?.domain || '',
      recordName,
      recordValue: samlConfiguration?.generatedVerification || '',
    };

    return (
      <div className='verify-domain'>
        <Formik
          initialValues={initialValues}
          enableReinitialize
          onSubmit={(values) => {
            samlConfiguration?.domain === values.domain
              ? validateDomain()
              : saveConfigurations({
                domain: values.domain,
              } as any);
          }}>
          {({ values, handleChange, handleBlur, dirty, isValid }) => (
            <Form>
              <FieldForm className='sso-domain'>
                <Field
                  component={Input}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.domain}
                  placeholder='Your Domain'
                  validate={checkDomain}
                  name='domain'
                  input={{ name: 'domain' }}
                />
                <ErrorMessage name='domain' component='div' className='field-error' />
              </FieldForm>
              {!dirty && (
                <FieldForm label='Copy this info into a new TXT record in your DNS file:' className='sso-dns'>
                  <FieldForm label='Record name'>
                    <Input value={recordName}>
                      <input disabled />
                      <ButtonCopy value={recordName} />
                    </Input>
                  </FieldForm>
                  <FieldForm label='Record value'>
                    <Input value={recordValue}>
                      <input disabled />
                      <ButtonCopy value={recordValue} />
                    </Input>
                  </FieldForm>
                </FieldForm>
              )}
              {samlConfiguration?.validated && !dirty ? (
                  <Button
                    className={classnames(
                      'sso-background-color sso-text-color',
                      dirty ? 'proceed-button' : 'validate-button',
                      samlConfiguration?.validated && 'validated',
                    )}
                    disabled={dirty && !isValid}
                    type='submit'
                    loading={isSaving}>
                      Validated
                  </Button>
              ) : (
                  <Button
                    className={classnames(
                      'sso-background-color sso-text-color',
                      dirty ? 'proceed-button' : 'validate-button',
                      samlConfiguration?.validated && 'validated',
                    )}
                    disabled={dirty && !isValid}
                    type='submit'
                    loading={isSaving}>
                    {dirty ? 'Proceed' : 'Validate'}
                  </Button>
                )}

              <div className='validation-error'>{domainValidationError}</div>
            </Form>
          )}
        </Formik>
      </div>
    );
  }

  render() {
    const { samlConfiguration, history } = this.props;

    return (
      <div className='domain-configuration'>
        <div className='domain-configuration__container'>
          <div className='domain-configuration__guide'>
            <SsoGuide guide={guides.domain} steps={[]} />
          </div>
          <div className='content'>
            <div className='header'>
              <h4>Claim domain</h4>
            </div>
            {samlConfiguration?.generatedVerification ? this.renderVerificationFlow(history) : this.renderDomainClaim()}
          </div>
        </div>
      </div>
    );
  }
}

const mapDispatchProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      setConfiguration,
      saveConfigurations,
      validateDomain,
    },
    dispatch,
  );

const mapStateToProps = (state: ISsoConfigurationState) => {
  const { samlConfiguration, isLoading, isSaving, domainValidationError, redirect } = state;
  return {
    isLoading,
    isSaving,
    redirect,
    domainValidationError,
    samlConfiguration,
  };
};

type DispatchProps = ReturnType<typeof mapDispatchProps>;

type StateToProps = ReturnType<typeof mapStateToProps>;

export default withRouter<any, any>(
  connect<StateToProps, DispatchProps, {}, ISsoConfigurationState>(
  mapStateToProps,
  mapDispatchProps,
)(SsoDomainConfiguration));
