}

interface IBigButtonState {
  mountNode: null | HTMLDivElement;
}

export class BigButtons extends React.Component<IBigButton, IBigButtonState> {
  divRef: React.RefObject<HTMLInputElement>;

  state = {
    mountNode: null,
  };

  constructor(props: IBigButton) {
    super(props);
    this.divRef = React.createRef<HTMLInputElement>();
  }

  componentDidMount() {
    this.divRef.current && this.setState({ mountNode: this.divRef.current });
  }

  render() {
    const { buttons, value: incomeValue, name, onChange } = this.props;
    const { mountNode } = this.state;

    return (
      <div ref={this.divRef} className='big-buttons'>
        {buttons.map(({ icon, title, help, value, backgroundColor, disabled }, idx) => (
          <React.Fragment key={idx}>
            <label
              className={classnames(
                'big-button-item sso-border-color-hover',
                incomeValue === value && 'active sso-border-color',
                disabled && 'disabled',
              )}>
              <input
                name={name}
                type='radio'
                value={value}
                disabled={disabled}
                checked={incomeValue === value}
                tabIndex={idx}
                onChange={onChange}
              />
              <span className='big-button-image' style={{ backgroundColor }}>
                <img src={icon} alt='' />
              </span>
              <span className='big-button-text'>
                {title.split('|').reduce((acc: Array<ReactElement<HTMLBRElement> | string>, curr, idx, arr) => {
                  acc.push(curr);
                  if (idx + 1 !== arr.length) {
                    acc.push(<br key={idx} />);
                  }
                  return acc;
                }, [])}
              </span>
              {/* <Popup mountNode={mountNode} content={help} trigger={<span className='big-button-help'>?</span>} /> */}
            </label>
          </React.Fragment>
        ))}
      </div>
    );
  }
}
import React, { FC } from 'react';
import classnames from 'classnames';
import { Button } from 'semantic-ui-react';
import './switcher.scss';

export interface ISwitcherButton {
  label: string;
  value: string;
}

interface ISwitcher {
  name: string;
  value: string;
  disabled?: boolean;
  style?: 'yellow' | 'gray' | 'red';
  buttons: Array<ISwitcherButton>;
  className?: string;
  onChange(event: React.ChangeEvent<any>): void;
}

export const Switcher: FC<ISwitcher> = ({ className, onChange, buttons, value, name, style, disabled }) => {
  const editRef = React.createRef<HTMLInputElement>();

  const onClick = (val: string) => {
    if (editRef.current) {
      editRef.current.value = val;
      var ev = new Event('input', { bubbles: true });
      editRef.current.dispatchEvent(ev);
    }
  };

  return (
    <div className={classnames(className, 'switcher', style && `style-${style}`)}>
      <input ref={editRef} onInput={onChange} name={name} type='hidden' />
      {buttons.map((button, idx) => (
        <Button
          disabled={disabled}
          basic
          key={idx}
          type='button'
          onClick={() => onClick(button.value)}
          className={classnames('switch-item', value === button.value && 'active')}>
          {button.label}
        </Button>
      ))}
    </div>
  );
};
