interface IInitialValues {
  type: TTypeAuth;
  ssoEndpoint?: string;
  configSaml: string;
  configFile?: File[];
  signRequest?: boolean;
  publicCertificate?: string;
}

const initialValues: IInitialValues = {
  type: 'saml',
  configSaml: 'auto',
};

const AcsUrlHelp = () => (
  <div>
    Copy this URL to the <b>ACS URL</b> section on the app configuration on your IDP <br />
  </div>
);

const AcsEntityIdHelp = () => (
  <div>
    Copy this ID to the <b>Entity ID</b> section for on the app configuration on your IDP <br />
  </div>
);

const SsoEndpointHelp = () => <div>Copy this URL from the SSO endpoint URL field on your IDP configuration</div>;

export interface IProps {
  overrideAcsUrl?: string;
  history?: any;
}

class IdpConfiguration extends React.Component<IProps & DispatchProps & StateToProps> {
  state = {
    step: 1,
  };

  componentDidUpdate() {
    if(this.props.redirect) {
      this.props.history.push('/');
      setTimeout(() => {
        this.props.redirectFalse()        
      }, 300);
    }
  }

  isValidConfiguration(
    configSaml: string,
    configFile: File[] | undefined,
    ssoEndpoint: string | undefined,
    publicCertificate: string | undefined,
  ) {
    return configSaml === 'auto' ? configFile && configFile.length : ssoEndpoint && publicCertificate;
  }

  render() {
    const {
      samlConfiguration,
      isSaving,
      spEntityId,
      overrideAcsUrl,
      acsUrl,
      saveConfigurations,
      saveConfigurationsAuto,
    } = this.props;
    const { step } = this.state;

    return (
      <div className='idp-configuration'>
        <Formik
          enableReinitialize
          initialValues={{
            ...initialValues,
            ...samlConfiguration,
            signRequest: samlConfiguration?.signRequest ? 'yes' : 'no',
            configSaml: samlConfiguration?.ssoEndpoint && samlConfiguration?.publicCertificate ? 'manual' : 'auto',
          }}
          onSubmit={({ type, ssoEndpoint, configSaml, publicCertificate, configFile, signRequest }) => {
            if (type === 'saml') {
              if (configSaml === 'auto') {
                saveConfigurationsAuto({ configFile } as any);
              } else {
                saveConfigurations({
                  ...samlConfiguration,
                  ssoEndpoint,
                  configSaml,
                  publicCertificate,
                  signRequest: signRequest === 'yes',
                } as any);
              }
            }
          }}>
          {({
            values: { type, configSaml, configFile, ssoEndpoint, publicCertificate, signRequest },
            handleChange,
            setFieldValue,
            handleBlur,
            dirty,
          }) => (
              <Form>
                <div className='idp-configuration__container'>
                  <div className='idp-configuration__list'>
                    <h4>Select your IDP</h4>
                    <BigButtons buttons={typeSSOs} name='type' value={type} onChange={handleChange} />
                  </div>
                  <div className='idp-configuration__guide'>
                    <SsoGuide guide={guides[type]} steps={configurationGuides[type]} />
                  </div>
                  <div className='idp-configuration__inputs'>
                    <div className={`header header-step-${step}`}>
                      <hr className='progress-step-1 sso-border-color' />
                      <hr
                        className={classnames('progress-step-2', step === 2 && 'progress-step-2-active sso-border-color')}
                      />
                      <h4>Configure Your IDP</h4>
                      <span>Step {step}</span>
                    </div>
                    {step === 1 && (
                      <div className='step1'>
                        <FieldForm className='field read-only' label='ACS URL'>
                          <Input onChange={handleChange} value={overrideAcsUrl || samlConfiguration?.acsUrl}>
                            <input disabled />
                            <ButtonCopy value={overrideAcsUrl || samlConfiguration?.acsUrl} />
                            <IconHelp content={AcsUrlHelp} />
                          </Input>
                        </FieldForm>
                        <FieldForm className='field read-only' label='Entity ID'>
                          <Input onChange={handleChange} value={samlConfiguration?.spEntityId}>
                            <input disabled />
                            <ButtonCopy value={samlConfiguration?.spEntityId} />
                            <IconHelp content={AcsEntityIdHelp} />
                          </Input>
                        </FieldForm>
                        <div className='button-next' onClick={() => this.setState({ step: 2 })}>
                          NEXT <ArrowBackSVG />
                        </div>
                      </div>
                    )}

                    {step === 2 && (
                      <div className='step2'>
                        <FieldForm className='field'>
                          <Switcher
                            buttons={configurationButtons}
                            value={configSaml}
                            onChange={handleChange}
                            name='configSaml'
                            style='gray'
                          />
                        </FieldForm>
                        {configSaml === 'auto' ? (
                          <FieldForm className='field' label='Metadata File'>
                            {configFile ? (
                              configFile[0].name
                            ) : (
                                <Dropzone
                                  onDrop={(acceptedFiles) => setFieldValue('configFile', acceptedFiles)}
                                  accept='text/xml'>
                                  {({ getRootProps, getInputProps }) => (
                                    <section className='drag-and-drop'>
                                      <div className='drag-and-drop-container'>
                                        <div {...getRootProps()}>
                                          <input {...getInputProps()} />
                                          <div className='bold'>Drag & Drop</div>
                                          <p>
                                            Click or drop an XML file
                                        <br /> with your configurations
                                      </p>
                                        </div>
                                      </div>
                                    </section>
                                  )}
                                </Dropzone>
                              )}
                          </FieldForm>
                        ) : (
                            <>
                              <FieldForm className='field sso-field' label='SSO Endpoint'>
                                <Field
                                  name='ssoEndpoint'
                                  placeholder='URL from the SSO'
                                  input={{ name: 'ssoEndpoint' }}
                                  value={ssoEndpoint}
                                  onBlur={handleBlur}
                                  onChange={handleChange}
                                  component={Input}
                                  validate={checkUrl}
                                  icon={<IconHelp content={SsoEndpointHelp} />}
                                />
                                <ErrorMessage name='ssoEndpoint' component='div' className='field-error' />
                              </FieldForm>
                              <FieldForm className='field_certificate' label='Public Certificate'>
                                <Field
                                  rows={5}
                                  id='publicCertificate'
                                  placeholder='Provide Public Certificate'
                                  name='publicCertificate'
                                  value={publicCertificate}
                                  onBlur={handleBlur}
                                  onChange={handleChange}
                                  validate={checkString}
                                  component={TextArea}
                                />
                                <ErrorMessage name='publicCertificate' component='div' className='field-error' />
                              </FieldForm>
                            </>
                          )}
                        <div className='button-prev' onClick={() => this.setState({ step: 1 })}>
                          <ArrowBackSVG /> BACK
                      </div>
                      </div>
                    )}
                    {step === 2 && (
                      <Button
                        type='submit'
                        className={
                          'field save sso-text-color sso-background-color' +
                          (!dirty && this.isValidConfiguration(configSaml, configFile, ssoEndpoint, publicCertificate)
                            ? ' validated'
                            : '')
                        }
