export interface IProps {
  context?: ContextOptions;
  overrideAcsUrl?: string;
  rootDir: string;
  history: any;
  noHeader?: boolean;
}

class SsoConfigurationContainer extends React.Component<IProps & RouteComponentProps & DispatchProps & StateToProps> {
  componentDidMount() {
    const { context, setContext, loadConfigurations, setRootDir } = this.props;

    setContext(context);
    setRootDir(this.props.rootDir);
    loadConfigurations();
  }

  onEnabledDisabledChanged() {
    const { samlConfiguration, setConfiguration, saveConfigurations } = this.props;

    const newConfig: any = {
      ...samlConfiguration,
      enabled: !samlConfiguration?.enabled,
    };

    if (samlConfiguration?.domain) {
      saveConfigurations(newConfig);
    } else {
      setConfiguration(newConfig);
    }
  }

  render() {
    const { isLoading, samlConfiguration, noHeader, location, history } = this.props;

    const enabled = samlConfiguration && samlConfiguration.enabled;
    const isEditMode = location.pathname !== '/';
    const onBackButtonClick = isEditMode ? history.goBack : undefined;

    if (isLoading) {
      return <div>Loading...</div>;
    }

    return (
      <div className='sso-configuration' id='sso-configuration'>
        {!noHeader && (
          <PageHeader
            title='SSO'
            subTitle='Configure single-sign-on with your own Identity Provider'
            onBackButtonClick={onBackButtonClick}
          />
        )}
        <div className='sso-switcher'>
          <span className={classnames(!samlConfiguration?.enabled && 'sso-switcher__disabled')}>Disabled</span>
          <Checkbox
            className='sso-switcher__checkbox'
            checked={samlConfiguration?.enabled}
            toggle
            onChange={() => this.onEnabledDisabledChanged()}
          />
          <span className={classnames(samlConfiguration?.enabled && 'sso-switcher__enabled')}>Enabled</span>
        </div>
        <div className='sso-content'>
          {!enabled && (
            <div className='sso-enabled-window'>
              <div className='sso-enabled-window__container'>
                <div className='sso-enabled-window__text'>
                  Enable sso and configure the settings to quickly use this functionality
                </div>
              </div>
            </div>
          )}

          {enabled && (
            <Switch>
              <Route exact path='/'>
                <SsoSteps />
              </Route>
              <Route path='/domain'>
                <SsoDomainConfiguration />
              </Route>
              <Route path='/idp'>
                <IdpConfiguration overrideAcsUrl={this.props.overrideAcsUrl} />
              </Route>
            </Switch>
          )}
        </div>
      </div>
    );
  }
}

