import React from 'react';
import Logger from '../../helpers/logger';
import { ContextConsumer, ContextOptions } from '../../providers/context';

export interface IProps {
  requiredRoles?: string[];
  requiredPermissions?: string[];
}

export default class AuthorizedContent extends React.Component<IProps, {}> {

  isAuthorized(context: ContextOptions) {
    if (this.props.requiredPermissions) {
      if (!context.currentUserPermissions || context.currentUserPermissions.length == 0) {
        Logger.info('No permissions for user. Required permissions are - ', this.props.requiredPermissions);
        return false;
      }

      for (const permission of this.props.requiredPermissions) {
        if (!context.currentUserPermissions.includes(permission)) {
          Logger.info(`Permissions ${permission} is missing from the list`);
          return false;
        }
      }
    }

    if (this.props.requiredRoles) {
      if (!context.currentUserRoles || context.currentUserRoles.length == 0) {
        Logger.info('No roles for user. Required roles are - ', this.props.requiredPermissions);
        return false;
      }

      for (const role of this.props.requiredRoles) {
        if (!context.currentUserRoles.includes(role)) {
          Logger.info(`Role ${role} is missing from the list`);
          return false;
        }
      }
    }

    return true;
  }

  render() {
    return (
      <ContextConsumer>
        {
          (context: any) => {
            return this.isAuthorized(context) ? this.props.children : <React.Fragment />
          }
        }
      </ ContextConsumer>
    )
  }
}
import { ISlackState, ISubscription, ISlackChanel, ICategoryEvent, ICreateOrUpdateData } from './slackTypes';
import { createSlice, createAction } from '@reduxjs/toolkit';

export const initialState: ISlackState = {
  context: null,
  isLoading: 0,
  isUpdating: false,
  channels: [],
  categories: [],
};

const { name, actions, reducer } = createSlice({
  name: 'slack',
  initialState,
  reducers: {
    setContext: (state, { payload }) => {
      state.context = payload;
    },
    getScopeDataAction: (state) => {
      state.isLoading++;
    },
    setCodeSuccessAction: (state) => {
      state.isLoading--;
    },
    getListOfEventsAction: (state) => {
      state.isLoading++;
    },
    getItemsDataAction: (state) => {
      state.isLoading++;
    },
    getSlackChannelsAction: (state) => {
      state.isLoading++;
    },
    getSlackChannelsSuccess: {
      prepare: (payload: ISlackChanel[]) => ({ payload }),
      reducer: (state, { payload }) => {
        state.isLoading--;
        state.channels = payload;
      },
    },
    scopeDataSuccess: {
      prepare: ({ clientId }: { clientId?: string }) => ({
        payload: { clientId },
      }),
      reducer: (state, { payload: { clientId } }) => {
        state.isLoading--;
        state.clientId = clientId;
      },
    },
    setCodeAction: {
      prepare: (code: string) => ({ payload: code }),
      reducer: (state) => {
        state.isLoading++;
      },
    },
    getItemsDataSuccess: {
      prepare: (payload: ICategoryEvent[]) => ({ payload }),
      reducer: (state, { payload }) => {
        state.categories = payload;
        state.isLoading--;
      },
    },
    getListOfEventsSuccess: {
      prepare: (payload?: ISubscription) => ({ payload }),
      reducer: (state, { payload }) => {
        state.subscription = payload;
        state.isLoading--;
      },
    },
    createOrUpdateSubscriptionAction: {
      prepare: (payload: ICreateOrUpdateData) => ({ payload }),
      reducer: (state) => {
        state.isUpdating = true;
      },
    },
    createOrUpdateSubscriptionSuccess: (state) => {
      state.isUpdating = false;
    },
  },
});

export const deleteSubscriptionAction = createAction<string>(`${name}/deleteSubscriptionAction`);

export const deleteNotificationAction = createAction<{ itemId: string; eventKey: string }>(
  `${name}/deleteNotificationAction`,
);

export { reducer };

export const {
  setContext,
  setCodeAction,
  scopeDataSuccess,
  getItemsDataAction,
  getScopeDataAction,
  getItemsDataSuccess,
  setCodeSuccessAction,
  getListOfEventsAction,
  getListOfEventsSuccess,
  getSlackChannelsAction,
  getSlackChannelsSuccess,
  createOrUpdateSubscriptionAction,
  createOrUpdateSubscriptionSuccess,
} = actions;
import { takeEvery, put } from 'redux-saga/effects';
import {
  getScopeDataAction,
  scopeDataSuccess,
  setCodeAction,
  setCodeSuccessAction,
  getListOfEventsAction,
  getListOfEventsSuccess,
  getItemsDataAction,
  getItemsDataSuccess,
  createOrUpdateSubscriptionAction,
  getSlackChannelsAction,
  getSlackChannelsSuccess,
  deleteSubscriptionAction,
  createOrUpdateSubscriptionSuccess,
  deleteNotificationAction,
} from './slackReducer';
import { getContextFromRedux } from '../../helpers/getContext';
import { Get, Post, Patch, Delete } from '../../helpers/fetch';
import Logger from '../../helpers/logger';
import { TCreateOrUpdateSubscription, TDeleteSubscription, TSetCodeFunction, TDeleteNotification } from './slackTypes';

const rootPath = '/integrations/resources/configurations/v1/slack';

function* getScopeDataFunction() {
  const context = yield getContextFromRedux();
  try {
    const { clientId } = yield Get(context, `${rootPath}/applications`);
    yield put(scopeDataSuccess({ clientId }));
  } catch (e) {
    Logger.error(e);
    yield put(scopeDataSuccess({}));
  }
}

function* setCodeFunction({ payload }: TSetCodeFunction) {
  const context = yield getContextFromRedux();
  try {
    yield Post(context, `${rootPath}/applications/registrations`, { code: payload });
    yield put(setCodeSuccessAction());
  } catch (e) {
    Logger.error(e);
  }
}

function* getListOfEventsFunction() {
  const context = yield getContextFromRedux();
  try {
    const result = yield Get(context, rootPath);
    yield put(getListOfEventsSuccess(result));
  } catch (e) {
    Logger.error(e);
    yield put(getListOfEventsSuccess());
  }
}

function* getSlackChannelsFunction() {
  const context = yield getContextFromRedux();
  try {
    const categories = yield Get(context, `${rootPath}/channels`);
    yield put(getSlackChannelsSuccess(categories || []));
  } catch (e) {
    Logger.error(e);
    yield put(getSlackChannelsSuccess([]));
  }
}

function* getItemsDataFunction() {
  const context = yield getContextFromRedux();
  try {
    const channels = yield Get(context, '/event/resources/configurations/v1/categories');
    yield put(getItemsDataSuccess(channels));
  } catch (e) {
    Logger.error(e);
    yield put(getItemsDataSuccess([]));
  }
}

function* createOrUpdateSubscriptionFunction({ payload: { id, ...body } }: TCreateOrUpdateSubscription) {
  const context = yield getContextFromRedux();
  try {
    if (id) {
      yield Patch(context, `${rootPath}/subscription/${id}`, body);
    } else {
      yield Post(context, `${rootPath}/subscriptions`, body);
    }
  } catch (e) {
    Logger.error(e);
  }
  yield put(createOrUpdateSubscriptionSuccess());
}

function* deleteSubscriptionFunction({ payload }: TDeleteSubscription) {
  const context = yield getContextFromRedux();
  try {
    yield Delete(context, `${rootPath}/subscriptions/${payload}`);
    yield put(getListOfEventsAction());
  } catch (e) {
    Logger.error(e);
  }
}

function* deleteNotificationFunction({ payload: { itemId, eventKey } }: TDeleteNotification) {
  const context = yield getContextFromRedux();
  try {
    yield Delete(context, `${rootPath}/subscriptions/${itemId}/${eventKey}`);
  } catch (e) {
    Logger.error(e);
  }
}

export function* rootSaga() {
  yield takeEvery(getScopeDataAction, getScopeDataFunction);
  yield takeEvery(getSlackChannelsAction, getSlackChannelsFunction);
  yield takeEvery(setCodeAction, setCodeFunction);
  yield takeEvery(getListOfEventsAction, getListOfEventsFunction);
  yield takeEvery(getItemsDataAction, getItemsDataFunction);
  yield takeEvery(createOrUpdateSubscriptionAction, createOrUpdateSubscriptionFunction);
  yield takeEvery(deleteSubscriptionAction, deleteSubscriptionFunction);
  yield takeEvery(deleteNotificationAction, deleteNotificationFunction);
}
