        channelName && channelsNames.push(`#${channelName}`);
      });
      return channelsNames;
    }, [])

    if ( channelsNames.length > 2) {
      channelsNames.splice(3)
      channelsNames[2]+='...'
    }
    return channelsNames.join(', ');
  }

  render() {
    const {
      addEvent,
      onEdit,
      onDelete,
      channels,
      subscription: { slackSubscriptions },
    } = this.props;
    return (
      <div className='slack-subscriptions'>
        <div className='slack-subscriptions-title'>
          <h4>Event Subscription </h4>
          <FButton onClick={addEvent}>ADD EVENT SUBSCRIPTION</FButton>
        </div>
        <div className='slack-subscriptions-list'>
          {slackSubscriptions.map(({ id, isActive, slackEvents }) => (
            slackEvents.length ? <div key={id} className='slack-subscriptions-item'>
              <div>
                <Icon name={isActive ? 'check' : 'info circle'} className='slack-subscriptions-icon' />
                <strong>{slackEvents.map(({ eventKey }) => eventKey).join(', ')}</strong>
                events to
                <strong>
                  {this.createChannelsNamesList(slackEvents)}
                </strong>
              </div>
              <div>
                <FButton role='action' onClick={() => onEdit(id)} className='slack-subscriptions-action'>
                  <Icon name='pencil' />
                  EDIT
                </FButton>
                <FButton role='action' onClick={() => onDelete(id)} className='slack-subscriptions-action'>
                  <Icon name='close' />
                  DELETE
                </FButton>
              </div>
            </div> : <div/>
          ))}
        </div>
      </div>
    );
  }
}
