class SlackSubscriptionsListComponent extends React.Component<TSlackSubscriptionsListComponent> {
  componentDidMount() {
    const { getListOfEvents, getSlackChannels } = this.props;
    getSlackChannels();
    getListOfEvents();
  }

  componentDidUpdate({ isLoading: prevIsLoading }: TSlackSubscriptionsListComponent) {
    const { isLoading, channels, history } = this.props;
    if (prevIsLoading && !isLoading && !channels.length) {
      history.push('/auth');
    }
  }

  onCreateNew = () => {
    const { history } = this.props;
    history.push('/item');
  };

  onEdit = (id: string) => {
    const { history } = this.props;
    history.push(`/item/${id}`);
  };

  onDelete = (id: string) => {
    const { deleteSubscription } = this.props;
    deleteSubscription(id);
  };

  render() {
    const { isLoading, subscription, channels } = this.props;
    return isLoading ? (
      <Loader />
    ) : subscription?.slackSubscriptions.length ? (
      <ListSubscriptions
        channels={channels}
        subscription={subscription}
        addEvent={this.onCreateNew}
        onDelete={this.onDelete}
        onEdit={this.onEdit}
      />
    ) : (
      <NoSubscriptions addEvent={this.onCreateNew} />
    );
  }
}

