type TCreateOrEditSubscription = TStateToProps & TDispatchProps & RouteComponentProps<{ itemId?: string }>;

class CreateOrEditSubscriptionComponent extends React.Component<
  TCreateOrEditSubscription,
  ICreateOrEditSubscriptionState
> {
  state = {
    isOpenAdvanced: false,
    eventsToDelete: []
  };
  componentDidMount() {
    const {
      categories,
      getItemsData,
      getListOfEvents,
      match: {
        params: { itemId },
      },
      isSubscriptionExists,
    } = this.props;
    !categories.length && getItemsData();
    itemId && !isSubscriptionExists && getListOfEvents();
  }

  componentDidUpdate({ isUpdating: prevIsUpdating }: TCreateOrEditSubscription) {
    const { isUpdating, history } = this.props;
    if (prevIsUpdating && !isUpdating) {
      history.push('/');
    }
  }

  deleteEvents = () => {
    this.state.eventsToDelete.forEach(({ parent, eventKey }) => {
      const { deleteNotification, categories, match: { params: { itemId } } } = this.props;
      if (parent) {
        itemId && deleteNotification({ itemId, eventKey });
      } else {
        itemId &&
        categories
          .filter(({ parent: parentId, key: id }) => parentId === parent)
          .forEach((item) => deleteNotification({ itemId, eventKey: item['data-event'] }));
      }
    })
  };

  render() {
    const {
      isLoading,
      channels,
      categories,
      createOrUpdateSubscription,
      match: {
        params: { itemId },
      },
      editData,
      isUpdating,
    } = this.props;
    const { isOpenAdvanced } = this.state;
    return isLoading ? (
      <Loader />
    ) : (
      <div className='slack-integration-item'>
        <h4>Event Subscription </h4>
        <Formik
          enableReinitialize
          initialValues={{ ...initialValues, ...editData }}
          onSubmit={({ isActive, channels, advanced }) => {
            createOrUpdateSubscription({
              id: itemId,
              isActive,
              slackEvents: Object.keys(advanced).map((id) => ({ ...advanced[id], channelIds: channels })),
            });
            this.deleteEvents();
          }
          }>
          {({ values, setFieldValue, handleChange }) => {
            const changeWebhookEvent = ({ key, parent, ...item }: DropdownItemProps) => {
              const { events } = values;
              let result: string[] = [...events];
              if (events.includes(key)) {
                // ID has present in values - need remove the value
                result = result.filter((val) => val !== key);
                this.setState({
                  eventsToDelete: [...this.state.eventsToDelete, {parent, eventKey: item['data-event']}]
                })
              } else {
                // The key is not in values
                if (parent) {
                  if (events.includes(parent)) {
                    // Need remove parent value from values and added another items to the values exclude the current item
                    const children = categories
                      .filter(({ parent: parentId, key: id }) => parentId === parent && id !== key)
                      .map(({ key }) => key);
                    result = [...result.filter((val) => val !== parent), ...children];
                  } else {
                    // If all child elements checked remove all from values and added only parent
                    const children = categories
                      .filter(
                        ({ parent: parentId, key: id }) => parentId === parent && id !== key && !events.includes(id),
                      )
                      .map(({ key }) => key);
                    result = [...result, key];
                  }
                } else {
                  // clean values from the child items and add only current item
                  const children = categories
                    .filter(({ parent: parentId, key: id }) => parentId === key && events.includes(id))
                    .map(({ key }) => key);
                  result = [...result.filter((val) => !children.includes(val)), key];
                }
              }
              setFieldValue('events', result);

              setFieldValue(
                'advanced',
                categories
                  .filter(
                    ({ key: id, parent: parentId }) => parentId && (result.includes(id) || result.includes(parentId)),
                  )
                  .reduce((acc, curr) => ({ ...acc, [curr.key]: { eventKey: curr['data-event'] } }), {}),
              );
            };
            return (
              <Form>
                <div>
                  <FieldForm label='Channels to notify' className='slack-integration-field'>
                    <Dropdown
                      className='slack-integration-dropdown'
                      placeholder='Start typing for channels...'
                      fluid
                      value={values.channels}
                      multiple
                      search
                      selection
                      options={channels}
                      onChange={(e, { value, key, ...data }) => {
                        setFieldValue('channels', value);
                      }}
                    />
                  </FieldForm>
                  <FieldForm label='Events to notify' className='slack-integration-field'>
                    <FDropdown
                      fluid
                      search
                      multiple
                      selection
                      className='slack-integration-dropdown slack-integration-dropdown-checkboxes'
                      placeholder='Start typing for channels...'
                      renderLabel={(item) => {
                        if (!item.parent) {
                          return `${item.text}.*`;
                        } else if (!values.events.includes(item.key)) {
                          return null;
                        }
                        return item['data-event'];
                      }}
                      value={categories
                        .filter(({ key, parent }) => values.events.includes(key) || values.events.includes(parent))
                        .map(({ key }) => key)}
                      options={categories.map((category) => ({
                        ...category,
                        onClick: () => changeWebhookEvent(category),
                      }))}
                    />
                  </FieldForm>
                  {!!values.events.length && (
                    <FieldForm className={classnames('slack-integration-field', isOpenAdvanced && 'fluid')}>
                      <Accordion className='slack-integration-accordion'>
                        <Accordion.Title
                          active={isOpenAdvanced}
                          onClick={() => this.setState({ isOpenAdvanced: !isOpenAdvanced })}>
                          <Icon name={isOpenAdvanced ? 'chevron down' : 'chevron right'} />
                          <strong>Notification Customization</strong>Advanced
                        </Accordion.Title>
                        <Accordion.Content active={isOpenAdvanced}>
                          <Table columns={16} className='slack-integration-table-advantage'>
                            <Table.Header>
                              <Table.Row>
                                <Table.HeaderCell>Events</Table.HeaderCell>
                                <Table.HeaderCell>Channels</Table.HeaderCell>
                                <Table.HeaderCell>Messages</Table.HeaderCell>
                                <Table.HeaderCell></Table.HeaderCell>
                              </Table.Row>
                            </Table.Header>
                            <Table.Body>
                              {Object.keys(values.advanced).map((id) => {
                                const { eventKey, title, message } = values.advanced[id];
                                return (
                                  <Table.Row key={id}>
                                    <Table.Cell width={3}>
                                      <strong>{eventKey}</strong>
                                    </Table.Cell>
                                    <Table.Cell width={4}>
                                      <Input
                                        value={title || ''}
                                        fluid
                                        name={`advanced.${id}.title`}
                                        onChange={handleChange}
                                        placeholder='Default Channels'
                                      />
                                    </Table.Cell>
                                    <Table.Cell width={6}>
                                      <Input
                                        fluid
                                        placeholder='Default Message'
                                        value={message || ''}
                                        name={`advanced.${id}.message`}
                                        onChange={handleChange}
                                      />
                                    </Table.Cell>
                                    <Table.Cell width={3} textAlign='right'>
                                      <FButton
                                        role='action'
                                        onClick={() => {
                                          const item = categories.find(({ key }) => key === id);
                                          item && changeWebhookEvent(item);
                                        }}>
                                        <Icon name='close' className='slack-integration-icon-delete' /> DELETE
                                      </FButton>
                                    </Table.Cell>
                                  </Table.Row>
                                );
                              })}
                            </Table.Body>
                          </Table>
                        </Accordion.Content>
                      </Accordion>
                    </FieldForm>
                  )}
                </div>
                <div className='slack-integration-activation'>
                  <FButton
                    className={values.isActive ? 'slack-integration-active' : 'slack-integration-inActive'}
                    onClick={() => setFieldValue('isActive', !values.isActive)}
                    type='button'
                    role='action'>
                    <Icon name='check' className='slack-integration-icon-check' />
                    {values.isActive ? 'DEACTIVATE THE EVENT' : 'ACTIVATE THE EVENT'}
                  </FButton>
                  <IconHelp content={ActivateHelp} popupStyle={helpStyle} position='top left' />
                </div>
                <FButton
                  type='submit'
                  className='slack-integration-submit'
                  loading={isUpdating}
                  disabled={!values.events.length || !values.channels.length}>
                  {itemId ? 'UPDATE EVENT SUBSCRIPTION ' : 'ADD EVENT SUBSCRIPTION'}
                </FButton>
              </Form>
            );
          }}
        </Formik>
      </div>
    );
  }
}

const mapDispatchProps = (dispatch: Dispatch) =>
  bindActionCreators(
    {
      getItemsData: getItemsDataAction,
      createOrUpdateSubscription: createOrUpdateSubscriptionAction,
      getListOfEvents: getListOfEventsAction,
      deleteNotification: deleteNotificationAction,
    },
    dispatch,
  );

const mapStateToProps = (
  { isLoading, channels, categories, subscription, isUpdating }: ISlackState,
  {
    match: {
      params: { itemId },
    },
  }: RouteComponentProps<{ itemId?: string }>,
) => {
  const categoriesList = categories.reduce(
    (acc: DropdownItemProps[], { id: channelId, events, name }: ICategoryEvent) => [
      ...acc,
      {
        key: channelId,
        value: channelId,
        text: name,
        className: 'webhook-category',
      },
      ...(events || []).map(({ id, displayName, key }) => ({
        className: 'webhook-event',
        parent: channelId,
        key: id,
        value: id,
        'data-event': key,
        text: displayName || key,
      })),
    ],
    [],
  );

  const editData =
    !itemId || !subscription
      ? initialValues
      : subscription.slackSubscriptions
          .filter(({ id }) => id === itemId)
          .reduce((_, { isActive, slackEvents }) => {
            return {
              isActive,
              channels: slackEvents[0].channelIds,
              ...slackEvents.reduce(
                (acc: Pick<IInitialValues, 'events' | 'advanced'>, { eventKey, title, message }) => {
                  const event = categoriesList.find((val) => val['data-event'] === eventKey);
                  if (!event) {
                    return acc;
                  }
                  acc.events.push(event.key);
                  acc.advanced[event.key] = { eventKey, title, message };
                  return acc;
                },
                { events: [], advanced: {} },
              ),
            };
          }, {});

  return {
    isLoading: !!isLoading,
    isUpdating,
    editData,
    isSubscriptionExists: !!subscription,
    channels: channels.map(({ id, name }) => ({ key: id, text: `#${name}`, value: id })),
    categories: categoriesList,
  };
};

type TDispatchProps = ReturnType<typeof mapDispatchProps>;

type TStateToProps = ReturnType<typeof mapStateToProps>;

