export interface IReportsTableComponents<T> {
  TitleCell?: FunctionComponent<ITableCell<T>>;
  ScheduleCell?: FunctionComponent<ITableCell<T>>;
  StatusCell?: FunctionComponent<ITableCell<T>>;
  LastGenerationsCell?: FunctionComponent<ITableCell<T>>;
  CreatedAtCell?: FunctionComponent<ITableCell<T>>;
  ActionsCell?: FunctionComponent<ITableCell<T>>;
}

export interface IReportsTable extends Omit<ITable<IReportRecord>, 'rows'> {
  components?: IReportsTableComponents<IReportRecord>;
}

const mapper = ({ state: { reports } }: IReportsContextValue) => ({ reports });
type MapperType = ReturnType<typeof mapper>;

export class _ReportsTable extends React.Component<IReportsTable & MapperType> {
  buildColumns(): ITableColumns<IReportRecord>[] {
    const { components, columns } = this.props;
    if (columns) {
      return columns;
    }
    return [
      {
        name: 'name',
        displayName: 'Title',
        isSortable: true,
        isFiltarable: true,
        Cell: components?.TitleCell ?? ReportsTableCols.ReportTitleCell,
      },
      {
        name: 'schedule',
        displayName: 'Schedule',
        isSortable: true,
        isFiltarable: true,
        Cell: components?.ScheduleCell ?? ReportsTableCols.ReportScheduleCell,
      },
      {
        name: 'updatedAt',
        displayName: 'Last Generations',
        isSortable: true,
        isFiltarable: true,
        Cell: components?.LastGenerationsCell ?? ReportsTableCols.ReportDateCell,
      },
      {
        name: 'createdAt',
        displayName: 'Created At',
        isSortable: true,
        isFiltarable: true,
        Cell: components?.CreatedAtCell ?? ReportsTableCols.ReportDateCell,
      },
      {
        name: 'id',
        displayName: '',
        isSortable: false,
        isFiltarable: false,
        Cell: components?.ActionsCell ?? ReportsTableCols.ReportActionsCell,
      },
    ];
  }

  render() {
    const { reports, classes } = this.props;
    const tableProps: ITable<IReportRecord> = {
      columns: this.buildColumns(),
      rows: reports.data,
      classes,
    };
    return <>
      <Table {...tableProps}/>
      {reports.loading && <Loader active={true}/>}
    </>;
  }
}

