export interface IReportGenerateTab {
}

const generateTypes = [
  { label: 'PDF', value: 'pdf' },
  { label: 'JPEG', value: 'image' },
  { label: 'Inline', value: 'html' },
];

const mapper = ({
                  state: { reportById, generatingReportById, sendingReportById },
                  actions: { generateReportById, sendReportById, debounceRenderReportById },
                }: IReportsContextValue) => ({
  reportById,
  generatingReportById,
  generateReportById,
  debounceRenderReportById,
  sendingReportById,
  sendReportById,
});

export class _ReportGenerateTab extends React.Component<IReportGenerateTab & ReturnType<typeof mapper>, {
  generateType: string;
  sendTo: string,
  filters: any
}> {
  state = {
    generateType: 'pdf',
    sendTo: '',
    filters: {},
  };

  downloadReport = () => {
    const { reportById: { data: report }, generateReportById } = this.props;
    const { generateType } = this.state;
    if (report == null) {
      return;
    }
    generateReportById({
      id: report.id,
      name: report.name,
      responseType: generateType as any,
    });
  };

  sendReport = () => {
    const { reportById: { data: report }, sendReportById } = this.props;
    const { sendTo } = this.state;
    if (report == null) {
      return;
    }
    sendReportById({ templateId: report.id, to: [sendTo] });
  };

  handleParameterChanged = (newState: any) => {
    const { reportById: { data: report }, debounceRenderReportById } = this.props;
    if (report == null) {
      return;
    }
    this.setState(newState, () => {
      debounceRenderReportById({
        id: report.id,
        dataFilters: this.state.filters,
        responseType: 'html',
      });
    });
  };

  generateTextParameter = ({ name, label, defaultValue, placeholder, validation, validationError }: DataFilterText,
                           handleParameterChanged: (state: any) => void) => {
    return <Input key={name} name={name}
                  label={label}
                  className='mb-3'
                  fullWidth={true}
                  placeholder={placeholder}
                  value={(this.state.filters as any)[name] || defaultValue}
                  onChange={e => handleParameterChanged({
                    filters: {
                      ...this.state.filters,
                      [name]: e.target.value,
                    },
                  })}
    />;
  };

  generateNumberParameter = ({ name, label, defaultValue, placeholder }: DataFilterNumber,
                             handleParameterChanged: (state: any) => void) => {
    return <Input key={name} name={name}
                  label={label}
                  className='mb-3'
                  type='number'
                  fullWidth={true}
                  placeholder={placeholder}
                  value={(this.state.filters as any)[name] || defaultValue}
                  onChange={e => handleParameterChanged({
                    filters: {
                      ...this.state.filters,
                      [name]: e.target.value,
                    },
                  })}
    />;
  };


  generateSelectParameter = ({ name, label, defaultValue, placeholder, options }: DataFilterSelect,
                             handleParameterChanged: (state: any) => void) => {
    return <Form.Field key={name} className='mb-3' width={16}>
      <label>{label}</label>
      <Form.Select
        value={(this.state.filters as any)[name] || defaultValue || ''}
        placeholder={placeholder}
        options={options.map(t => ({text: t.label, value: t.value}))}
        fluid
        onChange={(e, data) => handleParameterChanged({
          filters: {
            ...this.state.filters,
            [name]: data.value,
          },
        })}/>
    </Form.Field>;
  };

  generateMultiSelectParameter = ({ name, label, defaultValue, placeholder, options }: DataFilterMultiSelect,
                                  handleParameterChanged: (state: any) => void) => {
    return <Form.Field key={name} className='mb-3' width={16}>
      <label>{label}</label>
      <Dropdown
        placeholder={placeholder}
        fluid
        search
        selection
        multiple
        value={(this.state.filters as any)[name] || defaultValue || ''}
        options={options.map(t => ({text: t.label, value: t.value}))}
        onChange={(e, data) => handleParameterChanged({
          filters: {
            ...this.state.filters,
            [name]: data.value,
          },
        })}
      />
    </Form.Field>;
  };

  generateParameters = (dataFilters: DataFilter[]) => {

    return dataFilters.map(filter => {
      switch (filter.type) {
        case 'text':
          return this.generateTextParameter(filter as DataFilterText, this.handleParameterChanged);
        case 'number':
          return this.generateNumberParameter(filter as DataFilterNumber, this.handleParameterChanged);
        case 'select':
          return this.generateSelectParameter(filter as DataFilterSelect, this.handleParameterChanged);
        case 'multi-select':
          return this.generateMultiSelectParameter(filter as DataFilterMultiSelect, this.handleParameterChanged);
      }
      return null;
    }).filter(d => d != null);
  };

  render() {
    const { sendTo } = this.state;
    const {
      generatingReportById: { loading: downloadLoading },
      sendingReportById: { loading: sendLoading },
      reportById: { data: report },
    } = this.props;
    if (!report) {
      return null;
    }
    return <>

      {report.dataFilters && <div className='fe-section-header'>
        <SettingSVG className='fe-section-header__icon'/>
        <span className='fe-section-header__title'>Parameters</span>
      </div>}
      <Form className='mb-4'>
        {report.dataFilters && this.generateParameters(report.dataFilters.map(d => ({ type: d.type, ...d.config })))}
      </Form>
      <div className='fe-generate-container'>
        <div className='fe-section-header'>
          <img alt='Generate Report' className='fe-section-header__icon' src={ShareIcon}/>
          <span className='fe-section-header__title'>Generate Report</span>
        </div>
        <Form className='mb-4'>
          <Form.Field>
            <label className='mb-3'>File Type</label>
            {generateTypes.map(({ label, value }) => <Checkbox
              key={value}
              radio
              label={label}
              name='generate_report_type'
              value={value}
              checked={this.state.generateType === value}
              onChange={(e, data) => this.setState({ generateType: data.value as string })}
            />)}
          </Form.Field>
        </Form>

        <Input className='mb-4'
               value={sendTo}
               onChange={(e => this.setState({ sendTo: e.target.value }))}
               action={{
                 children: 'Send',
                 disabled: sendTo.length == 0,
                 onClick: this.sendReport,
               }}
               placeholder='E-mail address...'
               fullWidth={true}
               disabled={sendLoading}
               loading={sendLoading}/>
        <Button fullWidth={true}
                onClick={this.downloadReport}
                disabled={downloadLoading}
                loading={downloadLoading}>
          Download
        </Button>
      </div>
    </>;
  }
}

export const ReportGenerateTab = withReportsApi(_ReportGenerateTab, mapper);
import React from 'react';
import moment from 'moment';
import cronParser from 'cron-parser';

export interface CronFields {
  intervalType: any;
  intervalCron: any;
  intervalDayInWeek: any;
  intervalDayInMonth: any;
  intervalHour: any;
  intervalMinutes: any;
}


export const INTERVAL_TYPE = [
  { text: 'Daily', value: 'daily' },
  { text: 'Weekly', value: 'weekly' },
  { text: 'Monthly', value: 'monthly' },
  { text: 'Custom', value: 'custom' },
];

export const INTERVAL_DAY_IN_WEEK = Array.apply(null, Array(7))
  .map((_, i) => moment(i, 'e').format('dddd'))
  .map((day, index) => ({ text: day, value: index + 1 }));

export const INTERVAL_DAY_IN_MONTH = Array.apply(null, Array(31)).map((_, i) => ({
  text: i + 1 > 28 ? <span>{`${i + 1}`} <span style={{ fontSize: 11, color: 'grey' }}>(not recommended)</span></span> : `${i + 1}`,
  value: i + 1,
}));

export const INTERVAL_HOURS = Array.apply(null, Array(24)).map((_, i) => ({ text: `${i}`, value: i }));

export const INTERVAL_MINUTES = Array.apply(null, Array(60)).map((_, i) => ({ text: `${i}`, value: i }));

export const DEFAULT_CRON = '0 0 12 * * 3';

export const cronToFields = (cronParam: string = DEFAULT_CRON, cronType?: string): CronFields => {
  let cron;
  const cronStr = cronParam;
  try {
    cron = cronParser.parseExpression(cronStr);
  } catch (e) {
    // default
    cron = cronParser.parseExpression(DEFAULT_CRON);
  }
  let { minute, hour, dayOfWeek, dayOfMonth, month } = (cron as any)._fields;

  let intervalType: any = INTERVAL_TYPE[1];
  let intervalDayInWeek: any = INTERVAL_DAY_IN_WEEK[0];
  let intervalDayInMonth: any = INTERVAL_DAY_IN_MONTH[0];
  let intervalHour: any = INTERVAL_HOURS[11];
  let intervalMinutes: any = INTERVAL_MINUTES[0];
  if (
    cronType === 'custom' ||
    month.length !== 12 ||
    minute.length !== 1 ||
    hour.length !== 1 ||
    cronStr.lastIndexOf('* * * * *') === cronStr.length - 9
  ) {
    intervalType = INTERVAL_TYPE[3]; // 'custom';
  } else {
    intervalType = INTERVAL_TYPE[0]; // 'daily';
    intervalHour = INTERVAL_HOURS[hour[0]];
    intervalMinutes = INTERVAL_MINUTES[minute[0]];
    if (dayOfMonth.length !== 31) {
      if (dayOfMonth.length === 1) {
        intervalType = INTERVAL_TYPE[2]; // 'monthly';
        intervalDayInMonth = INTERVAL_DAY_IN_MONTH[dayOfMonth[0] - 1];
      } else {
        intervalType = INTERVAL_TYPE[3]; // 'custom';
      }
    } else {
      if (dayOfWeek.length !== 8) {
        if (dayOfWeek.length === 1) {
          intervalType = INTERVAL_TYPE[1]; // 'weekly';
          intervalDayInWeek = INTERVAL_DAY_IN_WEEK[dayOfWeek[0] - 1];
        } else {
          intervalType = INTERVAL_TYPE[3]; // 'custom';
        }
      }
    }
  }

  return {
    intervalType:intervalType.value,
    intervalCron: cronStr,
    intervalDayInWeek: intervalDayInWeek.value,
    intervalDayInMonth: intervalDayInMonth.value,
    intervalHour: intervalHour.value,
    intervalMinutes: intervalMinutes.value,
  };
};

export const fieldsToCron = ({ intervalType, intervalCron, intervalDayInWeek, intervalDayInMonth, intervalHour, intervalMinutes }: CronFields) => {
  switch (intervalType) {
    case 'daily':
      return `0 ${intervalMinutes} ${intervalHour} * * *`;
    case 'weekly':
      return `0 ${intervalMinutes} ${intervalHour} * * ${intervalDayInWeek}`;
    case 'monthly':
      return `0 ${intervalMinutes} ${intervalHour} ${intervalDayInMonth} * *`;
    case 'custom':
      return intervalCron;
  }
};
import React from 'react';
import cronParser from 'cron-parser';
import * as cronUtils from '../../../../helpers/cronUtils';
import { CronFields } from '../../../../helpers/cronUtils';
import moment from 'moment';
