export interface IWebhooksTableComponents<T> {
  TitleCell?: FunctionComponent<ITableCell<T>>;
  EventsCell?: FunctionComponent<ITableCell<T>>;
  InvocationsCell?: FunctionComponent<ITableCell<T>>;
  CreatedAtCell?: FunctionComponent<ITableCell<T>>;
  StatusCell?: FunctionComponent<ITableCell<T>>;
  ActionsCell?: FunctionComponent<ITableCell<T>>;
}

export interface IWebhooksTable extends Omit<ITable<IWebhookRecord>, 'rows'> {
  components?: IWebhooksTableComponents<IWebhookRecord>
}

const mapper = ({ state: { webhooks } }: IWebhooksContextValue) => ({ webhooks });
type MapperType = ReturnType<typeof mapper>;

class _WebhooksTable extends React.Component<IWebhooksTable & MapperType> {
  buildColumns(): ITableColumns<IWebhookRecord>[] {
    const { components, columns } = this.props;
    if (columns) {
      return columns;
    }

    return [
      {
        name: 'displayName',
        displayName: 'Title',
        Cell: components?.TitleCell ?? WebhooksTableCols.TitleCell,
      },
      {
        name: 'eventKeys',
        displayName: 'Events',
        Cell: components?.EventsCell ?? WebhooksTableCols.EventsCell,
      },
      {
        name: 'invocations',
        displayName: 'Invocations',
        Cell: components?.InvocationsCell ?? WebhooksTableCols.InvocationsCell,
      },
      {
        name: 'createdAt',
        displayName: 'Created At',
        Cell: components?.CreatedAtCell ?? WebhooksTableCols.CreatedAtCell,
      },
      {
        name: 'isActive',
        displayName: '',
        Cell: components?.StatusCell ?? WebhooksTableCols.StatusCell,
      },
      {
        name: '_id',
        displayName: '',
        Cell: components?.ActionsCell ?? WebhooksTableCols.ActionsCell,
      },
    ];
  }

  render() {
    const { classes, webhooks } = this.props;
    const tableProps: ITable<IWebhookRecord> = {
      columns: this.buildColumns(),
      rows: webhooks.data,
      classes: classes,
      rowKey: '_id',
    };
    return <>
      <Table {...tableProps}/>
      {webhooks.loading && <Loader active={true}/>}
    </>;
  }
}

