import React from 'react';
import { IReportsProvider, ReportsProvider } from '../plugins/reports';
import { IWebhooksProvider, WebhooksProvider } from '../plugins/webhooks';
import { UILibrary, UILibraryProvider } from './FronteggContext';
import { ContextOptions, ContextProvider } from './context';

export type FronteggPluginNames = 'reports' | 'webhooks'
export type FronteggPlugin<K, T> = {
  type: K;
  props?: T
}
export type FronteggPluginTypes =
  | FronteggPlugin<'reports', IReportsProvider>
  | FronteggPlugin<'webhooks', IWebhooksProvider>

export interface IFronteggProvider {
  contextOptions: ContextOptions;
  plugins: FronteggPluginTypes[];
  uiLibrary: UILibrary;
}

const PluginsProviders: {
  key: FronteggPluginNames,
  defaultRoute: string,
  Provider: typeof ReportsProvider | typeof WebhooksProvider
}[] = [{
  key: 'reports',
  defaultRoute: '/reports',
  Provider: ReportsProvider,
}, {
  key: 'webhooks',
  defaultRoute: '/webhooks',
  Provider: WebhooksProvider,
}];

export class FronteggProvider extends React.Component<IFronteggProvider> {
  static defaultProps = {
    plugins: [],
    uiLibrary: 'semantic',
    routes: {},
  };

  render() {
    const { children, plugins, uiLibrary, contextOptions } = this.props;
    let components = children;
    const getPlugin = (plugin: FronteggPluginNames): FronteggPluginTypes | undefined => plugins.find(value => value.type === plugin);

    PluginsProviders.forEach(({ Provider, key, defaultRoute }) => {
      const plugin = getPlugin(key);
      if (plugin) {
        components = <Provider {...({ rootDir: defaultRoute, ...plugin.props })}>{components}</Provider>;
      }
    });
    return <UILibraryProvider value={uiLibrary || 'semantic'}>
      <ContextProvider value={contextOptions}>
        {components}
      </ContextProvider>
    </UILibraryProvider>;
  }
}
import React from 'react';
import { Button, ButtonProps as SemanticButtonProps } from 'semantic-ui-react';
import { IButtonProps } from './interfaces';

const mapper = (props: IButtonProps): SemanticButtonProps => {
  const { variant, fullWidth, ...rest } = props;
  return {
    primary: variant === 'primary' ? true : undefined,
    secondary: variant === 'secondary' ? true : undefined,
    color: variant === 'danger' ? 'red' : undefined,
    fluid: fullWidth,
    ...rest,
  };
};

export default class SemanticButton extends React.Component<IButtonProps> {
  render() {
    return <Button {...mapper(this.props)}/>;
  }
}
import React from 'react';
import { Form, Input, InputProps } from 'semantic-ui-react';
import { IInputProps } from './interfaces';


const mapper = (props: IInputProps): InputProps => {
  const {
    label,
    labelPosition,
    validateFunction,
    resetOnInvalid,
    invalidMessage,
    onInputBlur,
    onChange,
    isNumber,
    incrementFactor,
    fullWidth,
    ...rest
  } = props;
  return {
    ...rest,
    fluid: fullWidth,
  } as InputProps;
};

const checkValidation = (props: IInputProps) => {
  return true;
};
export default (props: IInputProps) => {
  const inputProps = mapper(props);

  const isValid = checkValidation(props);

  const input = <Input {...inputProps}
                       error={!isValid}
                       onChange={event => {
                         props.onChange?.(event, isValid);
                       }}/>;

  if (props.labelPosition == null || props.labelPosition === 'top' && props.label) {
    return <Form.Field>
      <label>{props.label}</label>
      {input}
    </Form.Field>;
  }
  return input;
}
